<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Application\SuperAgent\DTO; use App\Infrastructure\Core\AbstractDTO; class FileConvertProgressDTO extends AbstractDTO { protected int $current = 0; protected int $total = 0; protected float $percentage = 0.0; protected string $message = ''; public function __construct(mixed $data = Null, ?int $total = Null, ?float $percentage = Null, ?string $message = Null) { // IfthoneParameter is Array, Then useArrayInitialize if (is_array($data)) { parent::__construct($data); return; } // BackwardCompatible: IfPassed in is singleParameter, Then byOriginalMethodProcess/Handle if ($data !== Null) { $this->current = (int) $data; } if ($total !== Null) { $this->total = $total; } if ($percentage !== Null) { $this->percentage = $percentage; } if ($message !== Null) { $this->message = $message; } } public static function fromArray(array $data): self { return new self( $data['current'] ?? 0, $data['total'] ?? 0, $data['percentage'] ?? 0.0, $data['message'] ?? '' ); } // ====== Getters ====== public function getCurrent(): int { return $this->current; } public function getTotal(): int { return $this->total; } public function getPercentage(): float { return $this->percentage; } public function getMessage(): string { return $this->message; } // ====== Setters ====== public function setCurrent(int $current): self { $this->current = $current; return $this; } public function setTotal(int $total): self { $this->total = $total; return $this; } public function setPercentage(float $percentage): self { $this->percentage = $percentage; return $this; } public function setMessage(string $message): self { $this->message = $message; return $this; } } 