<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Application\SuperAgent\Event\Subscribe; use Delightful\BeDelightful\Application\SuperAgent\Service\AccountAppService; use Hyperf\Amqp\Annotation\Consumer; use Hyperf\Amqp\Message\ConsumerMessage; use Hyperf\Amqp\Result; use Hyperf\Contract\StdoutLoggerInterface; use PhpAmqpLib\Message\AMQPMessage; use Throwable; #[Consumer(exchange: 'organization_created_message', routingKey: 'organization_created_message', queue: 'organization_created_message', nums: 1)] class InitAccountMessageSubscriber extends ConsumerMessage { public function __construct( private readonly AccountAppService $accountAppService, private readonly StdoutLoggerInterface $logger ) { } public function consumeMessage($event, AMQPMessage $message): Result { try { $this->logger->debug(sprintf( 'ReceivedOrganizationCreateMessage，Event: %s', json_encode($event, JSON_UNESCAPED_UNICODE | JSON_UNESCAPED_SLASHES) )); // Extractmustwantinformation $organizationCode = $event['organization_code'] ?? ''; // ParameterValidate if (empty($organizationCode)) { $this->logger->error(sprintf( 'MessageParameterIncomplete, $organizationCode: %s', $organizationCode, )); return Result::ACK; } $this->accountAppService->initAccount($organizationCode); $this->logger->info(sprintf('Super DelightfulAccountAlreadyInitialize，Organizationcode: %s', $organizationCode)); return Result::ACK; } catch (Throwable $e) { $this->logger->error(sprintf( 'Process/HandleSuper DelightfulAccountMessageFailed: %s, event: %s', $e->getMessage(), json_encode($event, JSON_UNESCAPED_UNICODE | JSON_UNESCAPED_SLASHES) )); return Result::ACK; // instant use outputwrongalsoConfirmMessage，avoidMessageAccumulate } } } 