<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Application\BeAgent\Service; use App\Application\Chat\Service\DelightfulChatMessageAppService; use App\Application\File\Service\FileAppService; use App\Application\File\Service\FileCleanupAppService; use App\Domain\Chat\Service\DelightfulConversationDomainService; use App\Domain\Chat\Service\DelightfulTopicDomainService as DelightfulChatTopicDomainService; use App\Domain\Contact\Entity\ValueObject\DataIsolation; use App\Domain\Contact\Service\DelightfulDepartmentDomainService; use App\Domain\Contact\Service\DelightfulUserDomainService; use App\Domain\File\Service\FileDomainService; use App\Domain\LongTermMemory\Service\LongTermMemoryDomainService; use App\ErrorCode\GenericErrorCode; use App\Infrastructure\Core\Exception\BusinessException; use App\Infrastructure\Core\Exception\EventException; use App\Infrastructure\Core\Exception\ExceptionBuilder; use App\Infrastructure\Core\ValueObject\StorageBucketType; use App\Infrastructure\Util\Context\RequestContext; use App\Infrastructure\Util\Locker\LockerInterface; use App\Interfaces\Authorization\Web\DelightfulUserAuthorization; use Delightful\BeDelightful\Application\Chat\Service\ChatAppService; use Delightful\BeDelightful\Application\BeAgent\Event\Publish\StopRunningTaskPublisher; use Delightful\BeDelightful\Domain\BeAgent\Constant\AgentConstant; use Delightful\BeDelightful\Domain\BeAgent\Entity\ValueObject\CreationSource; use Delightful\BeDelightful\Domain\BeAgent\Entity\ValueObject\DeleteDataType; use Delightful\BeDelightful\Domain\BeAgent\Entity\ValueObject\WorkspaceArchiveStatus; use Delightful\BeDelightful\Domain\BeAgent\Event\StopRunningTaskEvent; use Delightful\BeDelightful\Domain\BeAgent\Service\ProjectDomainService; use Delightful\BeDelightful\Domain\BeAgent\Service\TaskDomainService; use Delightful\BeDelightful\Domain\BeAgent\Service\TopicDomainService; use Delightful\BeDelightful\Domain\BeAgent\Service\WorkspaceDomainService; use Delightful\BeDelightful\ErrorCode\BeAgentErrorCode; use Delightful\BeDelightful\Infrastructure\ExternalAPI\Sandbox\Volcengine\SandboxService; use Delightful\BeDelightful\Infrastructure\Utils\WorkDirectoryUtil; use Delightful\BeDelightful\Interfaces\BeAgent\DTO\Request\GetWorkspaceTopicsRequestDTO; use Delightful\BeDelightful\Interfaces\BeAgent\DTO\Request\SaveWorkspaceRequestDTO; use Delightful\BeDelightful\Interfaces\BeAgent\DTO\Request\WorkspaceListRequestDTO; use Delightful\BeDelightful\Interfaces\BeAgent\DTO\Response\MessageItemDTO; use Delightful\BeDelightful\Interfaces\BeAgent\DTO\Response\SaveWorkspaceResultDTO; use Delightful\BeDelightful\Interfaces\BeAgent\DTO\Response\TaskFileItemDTO; use Delightful\BeDelightful\Interfaces\BeAgent\DTO\Response\TopicListResponseDTO; use Delightful\BeDelightful\Interfaces\BeAgent\DTO\Response\WorkspaceItemDTO; use Delightful\BeDelightful\Interfaces\BeAgent\DTO\Response\WorkspaceListResponseDTO; use Hyperf\Amqp\Producer; use Hyperf\DbConnection\Db; use Hyperf\Logger\LoggerFactory; use Psr\Log\LoggerInterface; use Throwable; class WorkspaceAppService extends AbstractAppService { protected LoggerInterface $logger; public function __construct( protected DelightfulChatMessageAppService $delightfulChatMessageAppService, protected DelightfulDepartmentDomainService $delightfulDepartmentDomainService, protected WorkspaceDomainService $workspaceDomainService, protected DelightfulConversationDomainService $delightfulConversationDomainService, protected DelightfulUserDomainService $userDomainService, protected DelightfulChatTopicDomainService $delightfulTopicDomainService, protected FileAppService $fileAppService, protected TaskDomainService $taskDomainService, protected AccountAppService $accountAppService, protected SandboxService $sandboxService, protected LockerInterface $locker, protected ChatAppService $chatAppService, protected ProjectDomainService $projectDomainService, protected TopicDomainService $topicDomainService, protected Producer $producer, protected LoggerFactory $loggerFactory, protected FileCleanupAppService $fileCleanupAppService, protected FileDomainService $fileDomainService, protected LongTermMemoryDomainService $longTermMemoryDomainService ) { $this->logger = $loggerFactory->get(get_class($this)); } /** * getWorkspacelist.*/ public function getWorkspaceList(RequestContext $requestContext, WorkspaceListRequestDTO $requestDTO): WorkspaceListResponseDTO { // BuildQueryCondition $conditions = $requestDTO->buildConditions(); // IfNo/NonespecifiedUser IDAnd haveuserauthorizationinformation, UseCurrent user ID if (empty($conditions['user_id'])) { $conditions['user_id'] = $requestContext->getUserAuthorization()->getId(); } // CreateDataIsolationObject $dataIsolation = $this->createDataIsolation($requestContext->getUserAuthorization()); // throughDomainServicegetWorkspacelist $result = $this->workspaceDomainService->getWorkspacesByConditions( $conditions, $requestDTO->page, $requestDTO->pageSize, 'id', 'desc', $dataIsolation ); // setdefaultValue $result['auto_create'] = false; if (empty($result['list'])) { $workspaceEntity = $this->workspaceDomainService->createWorkspace( $dataIsolation, '', '' ); $result['list'] = [$workspaceEntity->toArray()]; $result['total'] = 1; $result['auto_create'] = true; } // ExtractAllWorkspace ID $workspaceIds = []; foreach ($result['list'] as $workspace) { if (is_array($workspace)) { $workspaceIds[] = $workspace['id']; } else { $workspaceIds[] = $workspace->getId(); } } $workspaceIds = array_unique($workspaceIds); // BatchgetWorkspaceStatus $currentUserId = $dataIsolation->getCurrentUserId(); $workspaceStatusMap = $this->topicDomainService->calculateWorkspaceStatusBatch($workspaceIds, $currentUserId); // Convert toResponseDTOAndPassed inStatusMapping return WorkspaceListResponseDTO::fromResult($result, $workspaceStatusMap); } /** * getWorkspaceDetails.*/ public function getWorkspaceDetail(RequestContext $requestContext, int $workspaceId): WorkspaceItemDTO { // CreateDataIsolationObject $dataIsolation = $this->createDataIsolation($requestContext->getUserAuthorization()); // getWorkspaceDetails $workspaceEntity = $this->workspaceDomainService->getWorkspaceDetail($workspaceId); if ($workspaceEntity === Null) { ExceptionBuilder::throw(BeAgentErrorCode::WORKSPACE_NOT_FOUND, 'workspace.workspace_not_found'); } // ValidateWorkspacewhetherattributeinCurrentuser if ($workspaceEntity->getUserId() !== $dataIsolation->getCurrentUserId()) { ExceptionBuilder::throw(BeAgentErrorCode::WORKSPACE_ACCESS_DENIED, 'workspace.access_denied'); } // calculateWorkspaceStatus $workspaceStatusMap = $this->topicDomainService->calculateWorkspaceStatusBatch([$workspaceId]); $workspaceStatus = $workspaceStatusMap[$workspaceId] ?? Null; // ReturnWorkspaceDetailsDTO return WorkspaceItemDTO::fromEntity($workspaceEntity, $workspaceStatus); } public function createWorkspace(RequestContext $requestContext, SaveWorkspaceRequestDTO $requestDTO): SaveWorkspaceResultDTO { // Get user authorization information $userAuthorization = $requestContext->getUserAuthorization(); // Create data isolation object $dataIsolation = $this->createDataIsolation($userAuthorization); $workspaceEntity = $this->workspaceDomainService->createWorkspace( $dataIsolation, '', $requestDTO->getWorkspaceName() ); return SaveWorkspaceResultDTO::fromId((int) $workspaceEntity->getId()); } public function updateWorkspace(RequestContext $requestContext, SaveWorkspaceRequestDTO $requestDTO): SaveWorkspaceResultDTO { // Get user authorization information $userAuthorization = $requestContext->getUserAuthorization(); // Create data isolation object $dataIsolation = $this->createDataIsolation($userAuthorization); if (empty($requestDTO->getWorkspaceId())) { ExceptionBuilder::throw(BeAgentErrorCode::WORKSPACE_NOT_FOUND); } $this->workspaceDomainService->updateWorkspace($dataIsolation, (int) $requestDTO->getWorkspaceId(), $requestDTO->getWorkspaceName()); return SaveWorkspaceResultDTO::fromId((int) $requestDTO->getWorkspaceId()); } /** * Save workspace (create or update). * @return SaveWorkspaceResultDTO Operation result, including workspace ID * @throws BusinessException Throws an exception if saving fails * @throws Throwable */ public function saveWorkspace(RequestContext $requestContext, SaveWorkspaceRequestDTO $requestDTO): SaveWorkspaceResultDTO { Db::beginTransaction(); try { // Get user authorization information $userAuthorization = $requestContext->getUserAuthorization(); // Create data isolation object $dataIsolation = $this->createDataIsolation($userAuthorization); // Prepare workspace entity if ($requestDTO->getWorkspaceId()) { // Update, currently only updates workspace name $this->workspaceDomainService->updateWorkspace($dataIsolation, (int) $requestDTO->getWorkspaceId(), $requestDTO->getWorkspaceName()); Db::commit(); return SaveWorkspaceResultDTO::fromId((int) $requestDTO->getWorkspaceId()); } // SubmitTransaction Db::commit(); // Create, use provided workspace name if available; otherwise use default name $result = $this->initUserWorkspace($dataIsolation, $requestDTO->getWorkspaceName()); return SaveWorkspaceResultDTO::fromId($result['workspace']->getId()); } catch (EventException $e) { // RollbackTransaction Db::rollBack(); $this->logger->error(sprintf("Error creating new workspace event: %s\n%s", $e->getMessage(), $e->getTraceAsString())); ExceptionBuilder::throw(BeAgentErrorCode::CREATE_TOPIC_FAILED, $e->getMessage()); } catch (Throwable $e) { Db::rollBack(); $this->logger->error(sprintf("Error creating new workspace: %s\n%s", $e->getMessage(), $e->getTraceAsString())); ExceptionBuilder::throw(BeAgentErrorCode::CREATE_TOPIC_FAILED, 'topic.create_topic_failed'); } } /** * getWorkspace next Topiclist.*/ public function getWorkspaceTopics(RequestContext $requestContext, GetWorkspaceTopicsRequestDTO $dto): TopicListResponseDTO { // CreateDataIsolationObject $dataIsolation = $this->createDataIsolation($requestContext->getUserAuthorization()); // throughDomainServicegetWorkspaceTopiclist $result = $this->workspaceDomainService->getWorkspaceTopics( [$dto->getWorkspaceId()], $dataIsolation, true, $dto->getPageSize(), $dto->getPage(), $dto->getOrderBy(), $dto->getOrderDirection() ); // Convert toResponse DTO return TopicListResponseDTO::fromResult($result); } /** * getTaskAttachmentlist.*/ public function getTaskAttachments(DelightfulUserAuthorization $userAuthorization, int $taskId, int $page = 1, int $pageSize = 10): array { // CreateDataIsolationObject $dataIsolation = $this->createDataIsolation($userAuthorization); // getTaskAttachmentlist $result = $this->workspaceDomainService->getTaskAttachments($taskId, $dataIsolation, $page, $pageSize); // Process/HandleFile URL $list = []; $organizationCode = $userAuthorization->getOrganizationCode(); $fileKeys = []; // TraverseAttachmentlist, UseTaskFileItemDTOProcess/Handle foreach ($result['list'] as $entity) { // CreateDTO $dto = new TaskFileItemDTO(); $dto->fileId = (string) $entity->getFileId(); $dto->taskId = (string) $entity->getTaskId(); $dto->fileType = $entity->getFileType(); $dto->fileName = $entity->getFileName(); $dto->fileExtension = $entity->getFileExtension(); $dto->fileKey = $entity->getFileKey(); $dto->fileSize = $entity->getFileSize(); $dto->topicId = (string) $entity->getTopicId(); // add file_url field $fileKey = $entity->getFileKey(); if (! empty($fileKey)) { $fileLink = $this->fileAppService->getLink($organizationCode, $fileKey, StorageBucketType::SandBox); if ($fileLink) { $dto->fileUrl = $fileLink->getUrl(); } else { $dto->fileUrl = ''; } } else { $dto->fileUrl = ''; } // Judgefilekeywhetherrepeat, Ifrepeat, Then skip if (in_array($fileKey, $fileKeys)) { continue; } $fileKeys[] = $fileKey; $list[] = $dto->toArray(); } return [ 'list' => $list, 'total' => $result['total'], ]; } /** * DeleteWorkspace. * * @param RequestContext $requestContext request context * @param int $workspaceId Workspace ID * @return bool whetherDeleted successfully * @throws BusinessException IfuserNo permission or WorkspaceDoes not existinThen throwAbnormal*/ public function deleteWorkspace(RequestContext $requestContext, int $workspaceId): bool { // getuserauthorizationinformation $userAuthorization = $requestContext->getUserAuthorization(); // CreateDataIsolationObject $dataIsolation = $this->createDataIsolation($userAuthorization); // callDomainServiceExecuteDelete Db::beginTransaction(); try { //  first getWorkspace next AllProject ID, Used forDeleteLong-term memory $projectIds = $this->projectDomainService->getProjectIdsByWorkspaceId($dataIsolation, $workspaceId); // BatchDeleteProjectRelatedLong-term memory if (! empty($projectIds)) { $this->longTermMemoryDomainService->deleteMemoriesByProjectIds( $dataIsolation->getCurrentOrganizationCode(), AgentConstant::BE_DELIGHTFUL_CODE, $dataIsolation->getCurrentUserId(), $projectIds ); } // DeleteWorkspace $this->workspaceDomainService->deleteWorkspace($dataIsolation, $workspaceId); // DeleteWorkspace next Project $this->projectDomainService->deleteProjectsByWorkspaceId($dataIsolation, $workspaceId); // DeleteworkTopic $this->topicDomainService->deleteTopicsByWorkspaceId($dataIsolation, $workspaceId); // DeliverMessage, StopAllRun in Task $event = new StopRunningTaskEvent( DeleteDataType::WORKSPACE, $workspaceId, $dataIsolation->getCurrentUserId(), $dataIsolation->getCurrentOrganizationCode(), 'WorkspaceAlready be Delete' ); $publisher = new StopRunningTaskPublisher($event); $this->producer->produce($publisher); $this->logger->info(sprintf( 'AlreadyDeliverStopTaskMessage，Workspace ID: %d, EventID: %s', $workspaceId, $event->getEventId() )); Db::commit(); } catch (Throwable $e) { Db::rollBack(); $this->logger->error('DeleteWorkspaceFailed：' . $e->getMessage()); throw $e; } return true; } /** * getTaskDetails. * * @param RequestContext $requestContext request context * @param int $taskId TaskID * @return array TaskDetails * @throws BusinessException IfuserNo permission or TaskDoes not existinThen throwAbnormal*/ public function getTaskDetail(RequestContext $requestContext, int $taskId): array { // getuserauthorizationinformation $userAuthorization = $requestContext->getUserAuthorization(); // CreateDataIsolationObject $dataIsolation = $this->createDataIsolation($userAuthorization); // getTaskDetails $taskEntity = $this->workspaceDomainService->getTaskById($taskId); if (! $taskEntity) { ExceptionBuilder::throw(GenericErrorCode::SystemError, 'task.not_found'); } return $taskEntity->toArray(); } /** * getTopicMessagelist. * * @param int $topicId TopicID * @param int $page Page number * @param int $pageSize Page size * @param string $sortDirection Sort direction, SupportascAnddesc * @return array MessagelistAndTotal*/ public function getMessagesByTopicId(int $topicId, int $page = 1, int $pageSize = 20, string $sortDirection = 'asc'): array { // getMessagelist $result = $this->taskDomainService->getMessagesByTopicId($topicId, $page, $pageSize, true, $sortDirection); // Convert toResponseformat $messages = []; foreach ($result['list'] as $message) { $messages[] = new MessageItemDTO($message->toArray()); } $data = [ 'list' => $messages, 'total' => $result['total'], ]; // get topic information $topicEntity = $this->topicDomainService->getTopicWithDeleted($topicId); if ($topicEntity != Null) { $data['project_id'] = (string) $topicEntity->getProjectId(); $projectEntity = $this->getAccessibleProject($topicEntity->getProjectId(), $topicEntity->getUserId(), $topicEntity->getUserOrganizationCode()); $data['project_name'] = $projectEntity->getProjectName(); } return $data; } /** * setWorkspaceArchiveStatus. * * @param RequestContext $requestContext request context * @param array $workspaceIds Workspace IDArray * @param int $isArchived ArchiveStatus (0: un Archive, 1: already Archive) * @return bool whetherOperation successful*/ public function setWorkspaceArchived(RequestContext $requestContext, array $workspaceIds, int $isArchived): bool { // CreateDataIsolationObject $dataIsolation = $this->createDataIsolation($requestContext->getUserAuthorization()); $currentUserId = $dataIsolation->getCurrentUserId(); // ParameterValidate if (empty($workspaceIds)) { ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'workspace.ids_required'); } // ValidateArchiveStatusValuewhetherValid if (! in_array($isArchived, [ WorkspaceArchiveStatus::NotArchived->value, WorkspaceArchiveStatus::Archived->value, ])) { ExceptionBuilder::throw(GenericErrorCode::IllegalOperation, 'workspace.invalid_archive_status'); } // BatchUpdateWorkspaceArchiveStatus $success = true; foreach ($workspaceIds as $workspaceId) { // getWorkspaceDetails, ValidateAllpermission $workspaceEntity = $this->workspaceDomainService->getWorkspaceDetail((int) $workspaceId); // IfWorkspaceDoes not existin, Skip if (! $workspaceEntity) { $success = false; continue; } // ValidateWorkspacewhetherattributeinCurrentuser if ($workspaceEntity->getUserId() !== $currentUserId) { ExceptionBuilder::throw(GenericErrorCode::AccessDenied, 'workspace.not_owner'); } // callDomainServicesetArchiveStatus $result = $this->workspaceDomainService->archiveWorkspace( $requestContext, (int) $workspaceId, $isArchived === WorkspaceArchiveStatus::Archived->value ); if (! $result) { $success = false; } } return $success; } /** * getFileURLlist. * * @param DelightfulUserAuthorization $userAuthorization userauthorizationinformation * @param array $fileIds FileIDlist * @param string $downloadMode DownloadMode (download:Download, preview:Preview) * @param array $options Other options * @return array FileURLlist*/ public function getFileUrls(DelightfulUserAuthorization $userAuthorization, array $fileIds, string $downloadMode, array $options = []): array { // CreateDataIsolationObject $organizationCode = $userAuthorization->getOrganizationCode(); $result = []; foreach ($fileIds as $fileId) { // getFileEntity $fileEntity = $this->taskDomainService->getTaskFile((int) $fileId); if (empty($fileEntity)) { // IfFileDoes not existin, Skip continue; } // ValidateFilewhetherattributeinCurrentuser $projectEntity = $this->getAccessibleProject($fileEntity->getProjectId(), $userAuthorization->getId(), $organizationCode); $downloadNames = []; if ($downloadMode === 'download') { $downloadNames[$fileEntity->getFileKey()] = $fileEntity->getFileName(); } $fileLink = $this->fileAppService->getLink($organizationCode, $fileEntity->getFileKey(), StorageBucketType::SandBox, $downloadNames, $options); if (empty($fileLink)) { // IfgetURLFailed, Skip continue; } //  only addSuccessResult $result[] = [ 'file_id' => $fileId, 'url' => $fileLink->getUrl(), ]; } return $result; } public function getTopicDetail(int $topicId): string { $topicEntity = $this->workspaceDomainService->getTopicById($topicId); if (empty($topicEntity)) { return ''; } return $topicEntity->getTopicName(); } /** * getWorkspaceinformationthroughTopicIDCollection. * * @param array $topicIds TopicIDCollection (StringArray) * @return array byTopicIDForKey, WorkspaceinformationForValueassociatedArray*/ public function getWorkspaceInfoByTopicIds(array $topicIds): array { // ConvertStringIDForInteger $intTopicIds = array_map('intval', $topicIds); // callDomainServicegetWorkspaceinformation return $this->workspaceDomainService->getWorkspaceInfoByTopicIds($intTopicIds); } /** * RegisterConvert after PDFFileForScheduledClean.*/ public function registerConvertedPdfsForCleanup(DelightfulUserAuthorization $userAuthorization, array $convertedFiles): void { if (empty($convertedFiles)) { return; } $filesForCleanup = []; foreach ($convertedFiles as $file) { if (empty($file['oss_key']) || empty($file['filename'])) { continue; } $filesForCleanup[] = [ 'organization_code' => $userAuthorization->getOrganizationCode(), 'file_key' => $file['oss_key'], 'file_name' => $file['filename'], 'file_size' => $file['size'] ?? 0, // IfResponse in No/Nonesize，DefaultFor0 'source_type' => 'pdf_conversion', 'source_id' => $file['batch_id'] ?? Null, 'expire_after_seconds' => 7200, // 2 small time  after Expire 'bucket_type' => 'private', ]; } if (! empty($filesForCleanup)) { $this->fileCleanupAppService->registerFilesForCleanup($filesForCleanup); $this->logger->info('[PDF Converter] Registered converted PDF files for cleanup', [ 'user_id' => $userAuthorization->getId(), 'files_count' => count($filesForCleanup), ]); } } /** * InitializeuserWorkspace. * * @param DataIsolation $dataIsolation DataIsolationObject * @param string $workspaceName WorkspaceName, defaultFor" we Workspace" * @return array CreateResult, containingworkspaceAndtopicEntityObject, Andauto_createflag * @throws BusinessException IfCreateFailedThen throwAbnormal * @throws Throwable*/ private function initUserWorkspace( DataIsolation $dataIsolation, string $workspaceName = '' ): array { $this->logger->info('StartInitializeUserWorkspace'); Db::beginTransaction(); try { // Step 1: Initialize Delightful Chat Conversation [$chatConversationId, $chatConversationTopicId] = $this->chatAppService->initDelightfulChatConversation($dataIsolation); $this->logger->info(sprintf('InitializeBe Delightful, chatConversationId=%s, chatConversationTopicId=%s', $chatConversationId, $chatConversationTopicId)); // Step 2: Create workspace $this->logger->info('StartCreateDefaultWorkspace'); $workspaceEntity = $this->workspaceDomainService->createWorkspace( $dataIsolation, $chatConversationId, $workspaceName ); $this->logger->info(sprintf('CreateDefaultWorkspaceSuccess, workspaceId=%s', $workspaceEntity->getId())); if (! $workspaceEntity->getId()) { ExceptionBuilder::throw(GenericErrorCode::SystemError, 'workspace.create_workspace_failed'); } // CreatedefaultProject $this->logger->info('StartCreateDefaultProject'); $projectEntity = $this->projectDomainService->createProject( $workspaceEntity->getId(), '', $dataIsolation->getCurrentUserId(), $dataIsolation->getCurrentOrganizationCode(), '', '', Null, CreationSource::USER_CREATED->value ); $this->logger->info(sprintf('CreateDefaultProjectSuccess, projectId=%s', $projectEntity->getId())); // getWorkspaceDirectory $workDir = WorkDirectoryUtil::getWorkDir($dataIsolation->getCurrentUserId(), $projectEntity->getId()); // Step 4: Create default topic $this->logger->info('StartCreateDefaultTopic'); $topicEntity = $this->topicDomainService->createTopic( $dataIsolation, $workspaceEntity->getId(), $projectEntity->getId(), $chatConversationId, $chatConversationTopicId, '', $workDir ); $this->logger->info(sprintf('CreateDefaultTopicSuccess, topicId=%s', $topicEntity->getId())); // Step 5: Update workspace current topic if ($topicEntity->getId()) { // setWorkspaceinformation $workspaceEntity->setCurrentTopicId($topicEntity->getId()); $workspaceEntity->setCurrentProjectId($projectEntity->getId()); $this->workspaceDomainService->saveWorkspaceEntity($workspaceEntity); $this->logger->info(sprintf('Workspace%sAlreadySettingsCurrentTopic%s', $workspaceEntity->getId(), $topicEntity->getId())); // setProjectinformation $projectEntity->setCurrentTopicId($topicEntity->getId()); $projectEntity->setWorkspaceId($workspaceEntity->getId()); $projectEntity->setWorkDir($workDir); $this->projectDomainService->saveProjectEntity($projectEntity); $this->logger->info(sprintf('Project%sAlreadySettingsCurrentTopic%s', $projectEntity->getId(), $topicEntity->getId())); } Db::commit(); // Return creation result return [ 'workspace' => $workspaceEntity, 'topic' => $topicEntity, 'project' => $projectEntity, 'auto_create' => true, ]; } catch (Throwable $e) { Db::rollBack(); throw $e; } } } 