<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Domain\Agent\Entity\ValueObject; use function Hyperf\Translation\trans; enum BuiltinTool: string { // FileOperation (FileOperations) case ListDir = 'list_dir'; case ReadFiles = 'read_files'; case WriteFile = 'write_file'; case EditFile = 'edit_file'; case MultiEditFile = 'multi_edit_file'; case DeleteFile = 'delete_files'; case FileSearch = 'file_search'; case GrepSearch = 'grep_search'; // SearchExtract (SearchExtraction) case WebSearch = 'web_search'; case ImageSearch = 'image_search'; case ReadWebpagesAsMarkdown = 'read_webpages_as_markdown'; case DownloadFromUrls = 'download_from_urls'; case DownloadFromMarkdown = 'download_from_markdown'; // ContentProcess/Handle (ContentProcessing) case VisualUnderstanding = 'visual_understanding'; case GenerateImage = 'generate_image'; case ConvertToMarkdown = 'convert_to_markdown'; // SystemExecute (SystemExecution) case ShellExec = 'shell_exec'; case RunPythonSnippet = 'run_python_snippet'; /** * getTooluserfriendlyName.*/ public function getToolName(): string { return trans("builtin_tools.names.{$this->value}"); } /** * getTooluserfriendlyDescription.*/ public function getToolDescription(): string { return trans("builtin_tools.descriptions.{$this->value}"); } /** * getToolicon.*/ public function getToolIcon(): string { // temp time ReturnEmptyString, WaitbeforeendProvideicon content return ''; } /** * getToolCategory.*/ public function getToolCategory(): BuiltinToolCategory { return match ($this) { // FileOperation self::ListDir, self::ReadFiles, self::WriteFile, self::EditFile, self::MultiEditFile, self::DeleteFile, self::FileSearch, self::GrepSearch => BuiltinToolCategory::FileOperations, // SearchExtract self::WebSearch, self::ImageSearch, self::ReadWebpagesAsMarkdown, self::DownloadFromUrls, self::DownloadFromMarkdown => BuiltinToolCategory::SearchExtraction, // ContentProcess/Handle self::VisualUnderstanding, self::GenerateImage, self::ConvertToMarkdown => BuiltinToolCategory::ContentProcessing, // SystemExecute self::ShellExec, self::RunPythonSnippet => BuiltinToolCategory::SystemExecution, }; } /** * getToolType.*/ public static function getToolType(): BeDelightfulAgentToolType { return BeDelightfulAgentToolType::BuiltIn; } /** * getAllmustTool. * @return array<BuiltinTool>*/ public static function getRequiredTools(): array { return [ // BaseFileCRUD + DirectoryView self::ListDir, self::ReadFiles, self::WriteFile, self::EditFile, self::MultiEditFile, self::DeleteFile, self::FileSearch, self::GrepSearch, // InternetSearchAnd reading self::WebSearch, self::ImageSearch, self::ReadWebpagesAsMarkdown, // viewintuitive understanding self::VisualUnderstanding, // CommandRowExecute self::ShellExec, ]; } public static function isValidTool(string $tool): bool { return (bool) self::tryFrom($tool); } /** * JudgeToolwhetherFor mustTool.*/ public function isRequired(): bool { return in_array($this, self::getRequiredTools(), true); } } 
