<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\SuperMagic\Domain\Agent\Entity\ValueObject; enum SuperMagicAgentToolType: int { // 1: Built-in case BuiltIn = 1; // 2: official case Official = 2; // 3: Custom case Custom = 3; /** * getAllAvailableEnumValue. * @return array<int>*/ public static function getAvailableValues(): array { return array_map(fn ($case) => $case->value, self::cases()); } /** * getAllAvailableEnumValueString (Used forValidateRule).*/ public static function getValidationRule(): string { return implode(',', self::getAvailableValues()); } public function isRemote(): bool { return in_array($this, [self::Official, self::Custom], true); } public function isBuiltIn(): bool { return $this === self::BuiltIn; } } 