<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\SuperDelightful\Domain\Agent\Factory; use DateTime; use Delightful\SuperDelightful\Domain\Agent\Entity\SuperDelightfulAgentEntity; use Delightful\SuperDelightful\Domain\Agent\Entity\ValueObject\BuiltinAgent; use Delightful\SuperDelightful\Domain\Agent\Entity\ValueObject\SuperDelightfulAgentType; class BuiltinAgentFactory { /** * CreateBuilt-inAgentEntity.*/ public static function createEntity(BuiltinAgent $builtinAgent, string $organizationCode): SuperDelightfulAgentEntity { $entity = new SuperDelightfulAgentEntity(); // setBasicinformation $entity->setOrganizationCode($organizationCode); $entity->setCode($builtinAgent->value); $entity->setName($builtinAgent->getName()); $entity->setDescription($builtinAgent->getDescription()); $entity->setIcon($builtinAgent->getIcon()); $entity->setType(SuperDelightfulAgentType::Built_In); $entity->setEnabled(true); $entity->setPrompt($builtinAgent->getPrompt()); $entity->setTools([]); // setSystemCreateinformation $entity->setCreator('system'); $entity->setCreatedAt(new DateTime()); $entity->setModifier('system'); $entity->setUpdatedAt(new DateTime()); return $entity; } /** * CreateAllBuilt-inAgentEntity. * @return array<SuperDelightfulAgentEntity>*/ public static function createAllBuiltinEntities(string $organizationCode): array { $entities = []; foreach (BuiltinAgent::getAllBuiltinAgents() as $builtinAgent) { $entities[] = self::createEntity($builtinAgent, $organizationCode); } return $entities; } } 