<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Domain\BeAgent\Constant; use InvalidArgumentException; /** * ConvertTaskStatusEnum.*/ enum ConvertStatusEnum: string { case PENDING = 'pending'; case PROCESSING = 'processing'; case COMPLETED = 'completed'; case FAILED = 'failed'; /** * getAllValidStatusValue.*/ public static function getValidStatuses(): array { return array_map(fn (self $case) => $case->value, self::cases()); } /** * CheckStatuswhetherValid.*/ public static function isValid(string $status): bool { return in_array($status, self::getValidStatuses(), true); } /** * fromStringCreateEnumInstance.*/ public static function fromString(string $status): self { return match ($status) { 'pending' => self::PENDING, 'processing' => self::PROCESSING, 'completed' => self::COMPLETED, 'failed' => self::FAILED, default => throw new InvalidArgumentException("Invalid status: {$status}"), }; } } 
