<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Domain\BeAgent\Entity\ValueObject; /** * ProjectStatusEnum.*/ enum ProjectStatus: int { /** * ActiveStatus*/ case ACTIVE = 1; /** *  already Archive.*/ case ARCHIVED = 2; /** *  already Delete.*/ case DELETED = 3; /** * getStatusDescription.*/ public function getDescription(): string { return match ($this) { self::ACTIVE => 'Active', self::ARCHIVED => 'AlreadyArchive', self::DELETED => 'AlreadyDelete', }; } /** * whetherFor activeStatus*/ public function isActive(): bool { return $this === self::ACTIVE; } /** * whether already Archive.*/ public function isArchived(): bool { return $this === self::ARCHIVED; } /** * whether already Delete.*/ public function isDeleted(): bool { return $this === self::DELETED; } } 
