<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Domain\BeAgent\Entity\ValueObject; /** * TaskEventEnum.*/ enum TaskEvent: string { /** * TaskSuspend.*/ case SUSPENDED = 'suspended'; /** * Taskterminate.*/ case TERMINATED = 'terminated'; /** * getEventDescription.*/ public function getDescription(): string { return match ($this) { self::SUSPENDED => 'TaskSuspend', self::TERMINATED => 'Taskterminate', }; } /** * whetherForSuspendStatus*/ public function isSuspended(): bool { return $this === self::SUSPENDED; } /** * whetherFor terminateStatus*/ public function isTerminated(): bool { return $this === self::TERMINATED; } } 
