<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Domain\BeAgent\Entity\ValueObject; /** * TaskStatusValueObject*/ enum TaskStatus: string { /** * Wait in .*/ case WAITING = 'waiting'; /** * Run in .*/ case RUNNING = 'running'; /** * Completed.*/ case FINISHED = 'finished'; /** * Suspend.*/ case Suspended = 'suspended'; /** * terminate.*/ case Stopped = 'stopped'; /** * Error.*/ case ERROR = 'error'; /** * getStatusDescription.*/ public function getDescription(): string { return match ($this) { self::WAITING => 'Wait in ', self::RUNNING => 'Run in ', self::FINISHED => 'Completed', self::ERROR => 'Error', self::Suspended => 'Suspend', self::Stopped => 'terminate', }; } /** * getAllStatuslist. * * @return array<string, string> StatusValueandDescriptionMapping*/ public static function getList(): array { return [ self::WAITING->value => self::WAITING->getDescription(), self::RUNNING->value => self::RUNNING->getDescription(), self::FINISHED->value => self::FINISHED->getDescription(), self::ERROR->value => self::ERROR->getDescription(), self::Suspended->value => self::Suspended->getDescription(), self::Stopped->value => self::Stopped->getDescription(), ]; } /** * whetherFor final state*/ public function isFinal(): bool { return in_array($this, [self::FINISHED, self::ERROR, self::Stopped, self::Suspended], true); } /** * whetherFor activeStatus*/ public function isActive(): bool { return in_array($this, [self::WAITING, self::RUNNING], true); } } 
