<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\SuperMagic\Domain\Chat\DTO\Message\ChatMessage\Item; use App\Domain\Chat\Entity\AbstractEntity; /** * StepEntityClass.*/ class SuperAgentStep extends AbstractEntity { protected string $id = ''; /** * StepTitle.*/ protected string $title = ''; /** * StepStatus.*/ protected string $status = ''; public function __construct(array $step) { parent::__construct($step); } public function setId(string $id): void { $this->id = $id; } public function getId(): string { return $this->id; } public function getTitle(): string { return $this->title; } public function setTitle(string $title): void { $this->title = $title; } public function getStatus(): string { return $this->status; } public function setStatus(string $status): void { $this->status = $status; } public function toArray(): array { return [ 'id' => $this->id, 'title' => $this->title, 'status' => $this->status, ]; } } 