<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\SuperDelightful\Domain\Chat\DTO\Message\ChatMessage\Item\ValueObject; /** * memoryOperationActionEnum.*/ enum MemoryOperationAction: string { case ACCEPT = 'accept'; // Accept memory suggestion case REJECT = 'reject'; // Rejectedmemorysuggest /** * getOperationDescription.*/ public function getDescription(): string { return match ($this) { self::ACCEPT => 'Accept', self::REJECT => 'Rejected', }; } /** * getAllOperationValue.*/ public static function getAllValues(): array { return array_column(self::cases(), 'value'); } /** * CheckOperationwhetherValid.*/ public static function isValid(string $action): bool { return in_array($action, self::getAllValues(), true); } } 