<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Domain\Chat\DTO\Message\ChatMessage\Item\ValueObject; /** * memoryOperationScenarioEnum.*/ enum MemoryOperationScenario: string { case ADMIN_PANEL = 'admin_panel'; // managementBackground case MEMORY_CARD_QUICK = 'memory_card_quick'; // memorycardsliceshortcutOperation /** * getScenarioDescription.*/ public function getDescription(): string { return match ($this) { self::ADMIN_PANEL => 'managementBackground', self::MEMORY_CARD_QUICK => 'memorycardsliceshortcutOperation', }; } /** * getAllScenarioValue.*/ public static function getAllValues(): array { return array_column(self::cases(), 'value'); } /** * CheckScenariowhetherValid.*/ public static function isValid(string $scenario): bool { return in_array($scenario, self::getAllValues(), true); } /** * getdefaultScenario.*/ public static function getDefault(): self { return self::ADMIN_PANEL; } } 