<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\SuperDelightful\Domain\Share\Entity; use App\Infrastructure\Core\AbstractEntity; use DateTime; use Delightful\SuperDelightful\Domain\Share\Constant\ResourceType; use Delightful\SuperDelightful\Domain\Share\Constant\ShareAccessType; /** * GeneralResourceshareEntity.*/ class ResourceShareEntity extends AbstractEntity { /** * @var int Primary keyID*/ protected int $id = 0; /** * @var string ResourceID*/ protected string $resourceId = ''; /** * @var int ResourceType*/ protected int $resourceType = 0; /** * @var string ResourceName*/ protected string $resourceName = ''; /** * @var string share code*/ protected string $shareCode = ''; /** * @var int shareType*/ protected int $shareType = 0; /** * @var Null|string accessedpassword (Optional)*/ protected ?string $password = Null; /** * @var bool whetherEnablepasswordProtect*/ protected bool $isPasswordEnabled = false; /** * @var Null|string Expired at (Optional)*/ protected ?string $expireAt = Null; /** * @var int view count*/ protected int $viewCount = 0; /** * @var string CreatorUser ID*/ protected string $createdUid = ''; /** * @var string Update person User ID*/ protected string $updatedUid = ''; /** * @var string Organization code*/ protected string $organizationCode = ''; /** * @var string Target IDs (SpecificTargetType time Use) * format: [{"type": 1, "id": "123"}, {"type": 2, "id": "456"}] * type: 1-user, 2-Department, 3-group*/ protected string $targetIds = ''; /** * @var Null|array extraProperty/Attribute (Used forstoredExtensioninformation)*/ protected ?array $extra = Null; /** * @var bool whetherEnable (Invitation linkDedicated)*/ protected bool $isEnabled = true; /** * @var Null|string Created at*/ protected ?string $createdAt = Null; /** * @var Null|string Updated at*/ protected ?string $updatedAt = Null; /** * @var Null|string Deleted at*/ protected ?string $deletedAt = Null; /** * ConstructFunction.*/ public function __construct(array $data = []) { // defaultset $this->id = 0; // SetDefaultIDFor0 $this->viewCount = 0; $this->password = Null; $this->expireAt = Null; $this->deletedAt = Null; $this->targetIds = '[]'; // StorageForJSONString $this->extra = Null; $this->isEnabled = true; // DefaultEnable $this->isPasswordEnabled = false; // DefaultEnable $this->initProperty($data); } /** * getResourceTypeEnum.*/ public function getResourceTypeEnum(): ResourceType { return ResourceType::from($this->resourceType); } /** * setResourceTypeEnum.*/ public function setResourceTypeEnum(ResourceType $resourceType): self { $this->resourceType = $resourceType->value; return $this; } /** * getshareTypeEnum.*/ public function getShareTypeEnum(): ShareAccessType { return ShareAccessType::from($this->shareType); } /** * setshareTypeEnum.*/ public function setShareTypeEnum(ShareAccessType $shareType): self { $this->shareType = $shareType->value; return $this; } /** * getTarget IDArray.*/ public function getTargetIdsArray(): array { return json_decode($this->targetIds, true) ?: []; } /** * setTarget IDArray.*/ public function setTargetIdsArray(array $targetIds): self { $this->targetIds = json_encode($targetIds); return $this; } /** * ChecksharewhetherExpired*/ public function isExpired(): bool { if ($this->expireAt === Null) { return false; } $expireDateTime = new DateTime($this->expireAt); return $expireDateTime < new DateTime(); } /** * Checksharewhether already Delete.*/ public function isDeleted(): bool { return $this->deletedAt !== Null; } /** * ChecksharewhetherValid.*/ public function isValid(): bool { return ! $this->isExpired() && ! $this->isDeleted(); } /** * Addview count.*/ public function incrementViewCount(): void { ++$this->viewCount; } /** * Validatepassword*/ public function verifyPassword(string $inputPassword): bool { if (empty($this->password)) { return true; } // ActualApplicationShould inUsePasswordHasher enter RowsafepasswordValidate return $this->password === $inputPassword; } /** * Convert toArray.*/ public function toArray(): array { $result = [ 'id' => $this->id, 'resource_id' => $this->resourceId, 'resource_type' => $this->resourceType, 'resource_name' => $this->resourceName, 'share_code' => $this->shareCode, 'share_type' => $this->shareType, 'password' => $this->password, 'is_password_enabled' => $this->isPasswordEnabled, 'expire_at' => $this->expireAt, 'view_count' => $this->viewCount, 'created_uid' => $this->createdUid, 'updated_uid' => $this->updatedUid, 'organization_code' => $this->organizationCode, 'target_ids' => $this->targetIds, 'extra' => $this->extra, 'is_enabled' => $this->isEnabled, 'created_at' => $this->createdAt, 'updated_at' => $this->updatedAt, 'deleted_at' => $this->deletedAt, ]; // removeNullValue return array_filter($result, function ($value) { return $value !== Null; }); } // Getters and Setters public function getId(): int { return $this->id; } public function setId(int $id): self { $this->id = $id; return $this; } public function getResourceId(): string { return $this->resourceId; } public function setResourceId(string $resourceId): self { $this->resourceId = $resourceId; return $this; } public function getResourceType(): int { return $this->resourceType; } public function setResourceType(int $resourceType): self { $this->resourceType = $resourceType; return $this; } public function getResourceName(): string { return $this->resourceName; } public function setResourceName(string $resourceName): self { $this->resourceName = $resourceName; return $this; } public function getShareCode(): string { return $this->shareCode; } public function setShareCode(string $shareCode): self { $this->shareCode = $shareCode; return $this; } public function getShareType(): int { return $this->shareType; } public function setShareType(int $shareType): self { $this->shareType = $shareType; return $this; } public function getPassword(): ?string { return $this->password; } public function setPassword(?string $password): self { $this->password = $password; return $this; } public function getIsPasswordEnabled(): bool { return $this->isPasswordEnabled; } public function setIsPasswordEnabled(bool $isPasswordEnabled): self { $this->isPasswordEnabled = $isPasswordEnabled; return $this; } public function getExpireAt(): ?string { return $this->expireAt; } public function setExpireAt(?string $expireAt): self { $this->expireAt = $expireAt; return $this; } public function getViewCount(): int { return $this->viewCount; } public function setViewCount(int $viewCount): self { $this->viewCount = $viewCount; return $this; } public function getCreatedUid(): string { return $this->createdUid; } public function setCreatedUid(?string $createdUid): self { $this->createdUid = $createdUid; return $this; } public function getUpdatedUid(): string { return $this->updatedUid; } public function setUpdatedUid(string $updatedUid): self { $this->updatedUid = $updatedUid; return $this; } public function getOrganizationCode(): string { return $this->organizationCode; } public function setOrganizationCode(string $organizationCode): self { $this->organizationCode = $organizationCode; return $this; } public function getTargetIds(): string { return $this->targetIds; } public function setTargetIds(string $targetIds): self { $this->targetIds = $targetIds; return $this; } public function getExtra(): ?array { return $this->extra; } public function setExtra(?array $extra): self { $this->extra = $extra; return $this; } /** * getspecifiedextraProperty/AttributeValue.*/ public function getExtraAttribute(string $key, mixed $default = Null): mixed { return $this->extra[$key] ?? $default; } /** * setspecifiedextraProperty/AttributeValue.*/ public function setExtraAttribute(string $key, mixed $value): self { if ($this->extra === Null) { $this->extra = []; } $this->extra[$key] = $value; return $this; } public function getCreatedAt(): ?string { return $this->createdAt; } public function setCreatedAt(?string $createdAt): self { $this->createdAt = $createdAt; return $this; } public function getUpdatedAt(): ?string { return $this->updatedAt; } public function setUpdatedAt(?string $updatedAt): self { $this->updatedAt = $updatedAt; return $this; } public function getDeletedAt(): ?string { return $this->deletedAt; } public function setDeletedAt(?string $deletedAt): self { $this->deletedAt = $deletedAt; return $this; } public function getIsEnabled(): bool { return $this->isEnabled; } public function setIsEnabled(bool $isEnabled): self { $this->isEnabled = $isEnabled; return $this; } /** * Enableshare.*/ public function enable(): self { $this->isEnabled = true; return $this; } /** * Disableshare.*/ public function disable(): self { $this->isEnabled = false; return $this; } /** * GenerateRandomNumberpassword. * * @param int $length passwordLength, default5bit * @return string GenerateRandompassword*/ public static function generateRandomPassword(int $length = 5): string { $maxNumber = (int) str_repeat('9', $length); return str_pad((string) random_int(0, $maxNumber), $length, '0', STR_PAD_LEFT); } /** * GenerateRandompassword (SupportCustom kind sub, MainUsed forTest). * * @param int $length passwordLength * @param Null|int $seed Random kind sub, NullThen useSystemRandom * @return string GenerateRandompassword*/ public static function generateRandomPasswordWithSeed(int $length = 5, ?int $seed = Null): string { if ($seed !== Null) { mt_srand($seed); $password = ''; for ($i = 0; $i < $length; ++$i) { $password .= (string) mt_rand(0, 9); } return $password; } return self::generateRandomPassword($length); } } 