<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\SuperDelightful\Domain\SuperAgent\Entity\ValueObject; /** * DeleteDataTypeEnum * Used foridentifier be DeleteDataType, In order toQueryRelatedRun in Task*/ enum DeleteDataType: string { /** * Workspace.*/ case WORKSPACE = 'workspace'; /** * Project.*/ case PROJECT = 'project'; /** * Topic.*/ case TOPIC = 'topic'; /** * getTypeDescription.*/ public function getDescription(): string { return match ($this) { self::WORKSPACE => 'Workspace', self::PROJECT => 'Project', self::TOPIC => 'Topic', }; } /** * getAllTypelist. * * @return array<string, string> TypeValueandDescriptionMapping*/ public static function getList(): array { return [ self::WORKSPACE->value => self::WORKSPACE->getDescription(), self::PROJECT->value => self::PROJECT->getDescription(), self::TOPIC->value => self::TOPIC->getDescription(), ]; } } 