<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Domain\BeAgent\Entity\ValueObject; use App\Infrastructure\Core\Exception\ExceptionBuilder; use Delightful\BeDelightful\ErrorCode\BeAgentErrorCode; use function Hyperf\Translation\trans; /** * MemberRoleValueObject * * encapsulateMemberRolebusiness logicAndPermissionValidateRule*/ enum MemberRole: string { case OWNER = 'owner'; case MANAGE = 'manage'; case EDITOR = 'editor'; case VIEWER = 'viewer'; /** * fromStringCreateInstance.*/ public static function fromString(string $role): self { return match ($role) { 'owner' => self::OWNER, 'manage' => self::MANAGE, 'editor' => self::EDITOR, 'viewer' => self::VIEWER, default => ExceptionBuilder::throw(BeAgentErrorCode::INVALID_MEMBER_ROLE, trans('project.invalid_member_role')) }; } /** * fromValueCreateInstance.*/ public static function fromValue(string $value): self { return self::from($value); } /** * getValue*/ public function getValue(): string { return $this->value; } /** * whetherForAll person Role.*/ public function isOwner(): bool { return $this === self::OWNER; } /** * whetherFormanagement person Role.*/ public function isManager(): bool { return $this === self::MANAGE; } /** * whetherForEdit person Role.*/ public function isEditor(): bool { return $this === self::EDITOR; } /** * whetherForView person Role.*/ public function isViewer(): bool { return $this === self::VIEWER; } /** * whether have WritePermission.*/ public function hasWritePermission(): bool { return match ($this) { self::OWNER, self::MANAGE, self::EDITOR => true, self::VIEWER => false, }; } /** * whether have DeletePermission.*/ public function hasDeletePermission(): bool { return $this === self::OWNER; } /** * whether have managementPermission (InviteMemberã€setPermissionwait).*/ public function hasManagePermission(): bool { return match ($this) { self::OWNER, self::MANAGE => true, self::EDITOR, self::VIEWER => false, }; } /** * whether have sharePermission.*/ public function hasSharePermission(): bool { return match ($this) { self::OWNER, self::MANAGE, self::EDITOR => true, self::VIEWER => false, }; } /** * whetherCansetShortcut.*/ public function canSetShortcut(): bool { return $this->hasSharePermission(); } /** * getPermissionLevel (Larger numberPermissionexceedhigh).*/ public function getPermissionLevel(): int { return match ($this) { self::VIEWER => 1, self::EDITOR => 2, self::MANAGE => 3, self::OWNER => 4, }; } /** * contrastRolePermissionLevel.*/ public function isHigherOrEqualThan(self $other): bool { return $this->getPermissionLevel() >= $other->getPermissionLevel(); } /** * getAllAvailableRole.*/ public static function getAllRoles(): array { return [self::OWNER, self::MANAGE, self::EDITOR, self::VIEWER]; } /** * getAllRoleStringValue.*/ public static function getAllRoleValues(): array { return array_map(fn ($role) => $role->value, self::getAllRoles()); } /** * ValidatePermissionGrade.*/ public static function validatePermissionLevel(string $permission): MemberRole { $validPermissions = [ MemberRole::MANAGE->value, MemberRole::EDITOR->value, MemberRole::VIEWER->value, ]; if (! in_array($permission, $validPermissions, true)) { ExceptionBuilder::throw(BeAgentErrorCode::INVALID_MEMBER_ROLE, trans('project.invalid_member_role')); } return MemberRole::from($permission); } } 