<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Domain\BeAgent\Entity\ValueObject; /** * MemberStatusValueObject * * encapsulateMemberStatusbusiness logicAndValidateRule*/ enum MemberStatus: int { case INACTIVE = 0; case ACTIVE = 1; /** * whetherFor activationStatus*/ public function isActive(): bool { return $this === self::ACTIVE; } /** * whetherFor inactiveStatus*/ public function isInactive(): bool { return $this === self::INACTIVE; } /** * getDescription.*/ public function getDescription(): string { return match ($this) { self::ACTIVE => 'Active', self::INACTIVE => 'Inactive', }; } } 