<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\SuperMagic\Domain\SuperAgent\Entity\ValueObject; /** * Project Mode Value Object * ProjectModeValueObject*/ enum ProjectMode: string { case GENERAL = 'general'; // GeneralMode case PPT = 'ppt'; // PPTMode case DATA_ANALYSIS = 'data_analysis'; // DataAnalysisMode case REPORT = 'report'; // Research reportMode case MEETING = 'meeting'; // MeetingMode case SUMMARY = 'summary'; // SummaryMode case SUPER_DELIGHTFUL = 'super_magic'; // Super MagicMode /** * Get all available project modes. */ public static function getAllModes(): array { return [ self::GENERAL->value, self::PPT->value, self::DATA_ANALYSIS->value, self::REPORT->value, self::MEETING->value, self::SUMMARY->value, self::SUPER_DELIGHTFUL->value, ]; } /** * Get project mode description. */ public function getDescription(): string { return match ($this) { self::GENERAL => 'GeneralMode', self::PPT => 'PPTMode', self::DATA_ANALYSIS => 'DataAnalysisMode', self::REPORT => 'Research reportMode', self::MEETING => 'MeetingMode', self::SUMMARY => 'SummaryMode', self::SUPER_DELIGHTFUL => 'Super MagicMode', }; } } 