<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Domain\SuperAgent\Entity\ValueObject; /** * TaskFilesourceEnum.*/ enum TaskFileSource: int { case DEFAULT = 0; /** * homepage.*/ case HOME = 1; /** * ProjectDirectory.*/ case PROJECT_DIRECTORY = 2; /** * Agent. */ case AGENT = 3; case COPY = 4; /** * Move.*/ case MOVE = 6; /** * getsourceName.*/ public function getName(): string { return match ($this) { self::DEFAULT => 'Default', self::HOME => 'homepage', self::PROJECT_DIRECTORY => 'ProjectDirectory', self::AGENT => 'Agent', self::COPY => 'Copy', self::MOVE => 'Move', }; } /** * fromString or IntegerCreateEnumInstance.*/ public static function fromValue(int|string $value): self { if (is_string($value)) { $value = (int) $value; } return match ($value) { 1 => self::HOME, 2 => self::PROJECT_DIRECTORY, 3 => self::AGENT, 4 => self::COPY, 6 => self::MOVE, default => self::DEFAULT, }; } } 