<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Domain\SuperAgent\Repository\Model; use App\Infrastructure\Core\AbstractModel; use Hyperf\Database\Model\SoftDeletes; /** * ProjectModel.*/ class ProjectModel extends AbstractModel { use SoftDeletes; /** * softDeletefield.*/ public const DELETED_AT = 'deleted_at'; /** * Tablename.*/ protected ?string $table = 'magic_super_agent_project'; /** * Fillablefield.*/ protected array $fillable = [ 'id', 'user_id', 'user_organization_code', 'workspace_id', 'project_name', 'project_description', 'work_dir', 'project_status', 'current_topic_id', 'current_topic_status', 'is_collaboration_enabled', 'default_join_permission', 'project_mode', 'source', 'created_uid', 'updated_uid', ]; /** * fieldTypeConvert.*/ protected array $casts = [ 'id' => 'integer', 'workspace_id' => 'integer', 'is_collaboration_enabled' => 'integer', 'default_join_permission' => 'string', 'created_at' => 'datetime', 'updated_at' => 'datetime', 'deleted_at' => 'datetime', ]; /** * getProjectBelong toWorkspace.*/ public function workspace() { return $this->belongsTo(WorkspaceModel::class, 'workspace_id', 'id'); } /** * getProject next Topiclist.*/ public function topics() { return $this->hasMany(TopicModel::class, 'project_id', 'id'); } /** * getProject next Filelist.*/ public function files() { return $this->hasMany(ProjectFileModel::class, 'project_id', 'id'); } } 