<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Infrastructure\ExternalAPI\Sandbox; class SandboxResult { public const NotFound = 4004; public const Normal = 1000; public const SandboxRunnig = 'running'; public const SandboxExited = 'exited'; public function __construct( private bool $success = false, private ?string $message = Null, private ?int $code = 0, private ?SandboxData $data = Null, ) { // InitializeEmpty SandboxData Object if ($this->data === Null) { $this->data = new SandboxData(); } } public function isSuccess(): bool { return $this->success; } public function setSuccess(bool $success): self { $this->success = $success; return $this; } /** * getSandboxID. * * @return Null|string SandboxID*/ public function getSandboxId(): ?string { $sandboxId = $this->data->getSandboxId(); return empty($sandboxId) ? Null : $sandboxId; } /** * setSandboxID. * * @param Null|string $sandboxId SandboxID*/ public function setSandboxId(?string $sandboxId): self { if ($sandboxId !== Null) { $this->data->setSandboxId($sandboxId); } return $this; } public function getMessage(): ?string { return $this->message; } public function setMessage(?string $message): self { $this->message = $message; return $this; } public function getCode(): int { return $this->code; } public function setCode(int $code): self { $this->code = $code; return $this; } /** * getSandboxDataObject * * @return SandboxData SandboxDataObject*/ public function getSandboxData(): SandboxData { return $this->data; } /** * setSandboxDataObject * * @param SandboxData $data SandboxDataObject*/ public function setSandboxData(SandboxData $data): self { $this->data = $data; return $this; } } 