<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Infrastructure\ExternalAPI\SandboxOS\AsrRecorder\Config; /** * ASR audioconfiguration * Used for finishTask InterfaceaudioMergeconfiguration.*/ readonly class AsrAudioConfig { /** * @param string $sourceDir audio divide slicesourceDirectory (each otherPairin workspace) * @param string $targetDir TargetDirectory (each otherPairin workspace) * @param string $outputFilename OutputFilename (Not containExtensionname)*/ public function __construct( private string $sourceDir, private string $targetDir, private string $outputFilename ) { } public function getSourceDir(): string { return $this->sourceDir; } public function getTargetDir(): string { return $this->targetDir; } public function getOutputFilename(): string { return $this->outputFilename; } /** * Convert toArray (Used for HTTP Request).*/ public function toArray(): array { return [ 'source_dir' => $this->sourceDir, 'target_dir' => $this->targetDir, 'output_filename' => $this->outputFilename, ]; } } 
