<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\SuperDelightful\Infrastructure\ExternalAPI\SandboxOS\AsrRecorder\Config; /** * ASR NoteFileconfiguration * Used for finishTask InterfaceNoteFileProcess/Handleconfiguration.*/ readonly class AsrNoteFileConfig { /** * @param string $sourcePath sourceFile path (each otherPairin workspace) * @param string $targetPath TargetFile path (each otherPairin workspace) * @param string $action OperationType (rename_and_move)*/ public function __construct( private string $sourcePath, private string $targetPath, private string $action = 'rename_and_move' ) { } public function getSourcePath(): string { return $this->sourcePath; } public function getTargetPath(): string { return $this->targetPath; } public function getAction(): string { return $this->action; } /** * Convert toArray (Used for HTTP Request).*/ public function toArray(): array { return [ 'source_path' => $this->sourcePath, 'target_path' => $this->targetPath, 'action' => $this->action, ]; } } 