<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Interfaces\BeAgent\DTO\Response; use App\Infrastructure\Core\AbstractDTO; /** * CollaborationProjectCreatorlistResponse DTO.*/ class CollaborationCreatorListResponseDTO extends AbstractDTO { /** * @var CollaborationCreatorItemDTO[] Creatorlist*/ protected array $creators = []; /** * fromuserEntityArrayCreateResponseDTO. * * @param array $userEntities userEntityArray*/ public static function fromUserEntities(array $userEntities): self { $dto = new self(); $creatorItems = []; foreach ($userEntities as $userEntity) { $creatorItems[] = CollaborationCreatorItemDTO::fromUserEntity($userEntity); } $dto->setCreators($creatorItems); return $dto; } /** * CreateEmptyResponseDTO.*/ public static function fromEmpty(): self { return new self(); } /** * Convert toArray.*/ public function toArray(): array { return array_map(fn (CollaborationCreatorItemDTO $creator) => $creator->toArray(), $this->creators); } /** * getCreatorlist. * * @return CollaborationCreatorItemDTO[]*/ public function getCreators(): array { return $this->creators; } /** * setCreatorlist. * * @param CollaborationCreatorItemDTO[] $creators*/ public function setCreators(array $creators): self { $this->creators = $creators; return $this; } } 
