<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Interfaces\BeAgent\DTO\Response; class CreateBatchDownloadResponseDTO { /** * @var string Process/HandleStatus (ready|processing)*/ protected string $status; /** * @var string BatchKey*/ protected string $batchKey; /** * @var Null|string DownloadURL (StatusForready time Provide)*/ protected ?string $downloadUrl; /** * @var int FileQuantity*/ protected int $fileCount; /** * @var string Descriptioninformation*/ protected string $message; /** * ConstructFunction.*/ public function __construct( string $status, string $batchKey, ?string $downloadUrl = Null, int $fileCount = 0, string $message = '' ) { $this->status = $status; $this->batchKey = $batchKey; $this->downloadUrl = $downloadUrl; $this->fileCount = $fileCount; $this->message = $message; } /** * Convert toArray.*/ public function toArray(): array { return [ 'status' => $this->status, 'batch_key' => $this->batchKey, 'download_url' => $this->downloadUrl, 'file_count' => $this->fileCount, 'message' => $this->message, ]; } /** * getProcess/HandleStatus.*/ public function getStatus(): string { return $this->status; } /** * getBatchKey.*/ public function getBatchKey(): string { return $this->batchKey; } /** * getDownloadURL.*/ public function getDownloadUrl(): ?string { return $this->downloadUrl; } /** * getFileQuantity.*/ public function getFileCount(): int { return $this->fileCount; } /** * getDescriptioninformation.*/ public function getMessage(): string { return $this->message; } } 
