<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Interfaces\BeAgent\DTO\Response; /** * TopiccopiedStatusResponseDTO.*/ class DuplicateTopicStatusResponseDTO { /** * TaskID.*/ protected string $taskId; /** * TaskStatus (running, completed, failed).*/ protected string $status; /** * StatusMessage.*/ protected string $message; /** *  enter degreeinformation.*/ protected ?array $progress = Null; /** * Resultinformation (TaskComplete time ).*/ protected ?array $result = Null; /** * Errorinformation (TaskFailed time ).*/ protected ?string $error = Null; /** * ConstructFunction.*/ public function __construct( string $taskId, string $status, string $message = '', ?array $progress = Null, ?array $result = Null, ?string $error = Null ) { $this->taskId = $taskId; $this->status = $status; $this->message = $message; $this->progress = $progress; $this->result = $result; $this->error = $error; } /** * Convert toArray.*/ public function toArray(): array { $result = [ 'task_id' => $this->taskId, 'status' => $this->status, 'message' => $this->message, ]; if ($this->progress !== Null) { $result['progress'] = $this->progress; } if ($this->result !== Null) { $result['result'] = $this->result; } if ($this->error !== Null) { $result['error'] = $this->error; } return $result; } /** * fromArrayCreateDTOInstance.*/ public static function fromArray(array $data): self { return new self( $data['task_id'], $data['status'], $data['message'] ?? '', $data['progress'] ?? Null, $data['result'] ?? Null, $data['error'] ?? Null ); } /** * getTaskID.*/ public function getTaskId(): string { return $this->taskId; } /** * setTaskID.*/ public function setTaskId(string $taskId): self { $this->taskId = $taskId; return $this; } /** * getTaskStatus*/ public function getStatus(): string { return $this->status; } /** * setTaskStatus*/ public function setStatus(string $status): self { $this->status = $status; return $this; } /** * getStatusMessage.*/ public function getMessage(): string { return $this->message; } /** * setStatusMessage.*/ public function setMessage(string $message): self { $this->message = $message; return $this; } /** * get enter degreeinformation.*/ public function getProgress(): ?array { return $this->progress; } /** * set enter degreeinformation.*/ public function setProgress(?array $progress): self { $this->progress = $progress; return $this; } /** * getResultinformation.*/ public function getResult(): ?array { return $this->result; } /** * setResultinformation.*/ public function setResult(?array $result): self { $this->result = $result; return $this; } /** * getErrorinformation.*/ public function getError(): ?string { return $this->error; } /** * setErrorinformation.*/ public function setError(?string $error): self { $this->error = $error; return $this; } } 