<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Interfaces\BeAgent\DTO\Response; use App\Infrastructure\Core\AbstractDTO; class ProjectMembersResponseDTO extends AbstractDTO { /** * @var ProjectMemberItemDTO[] ProjectMemberlist*/ protected array $members = []; /** * fromMemberDataCreateResponseDTO. * * @param array $users userDataArray * @param array $departments DepartmentDataArray*/ public static function fromMemberData(array $users, array $departments): self { $dto = new self(); $members = []; // Process/HandleuserData foreach ($users as $userData) { $members[] = ProjectMemberItemDTO::fromUserData($userData); } // Process/HandleDepartmentData foreach ($departments as $departmentData) { $members[] = ProjectMemberItemDTO::fromDepartmentData($departmentData); } $dto->setMembers($members); return $dto; } /** * fromEmptyResultCreateResponseDTO.*/ public static function fromEmpty(): self { $dto = new self(); $dto->setMembers([]); return $dto; } /** * Convert toArray * By/According toAPIDocument, ReturnformatFor [[...members]], i.e. two-dimensionalArray, butEmptyMember time ReturnEmptyArray.*/ public function toArray(): array { $memberArrays = []; foreach ($this->members as $member) { $memberArrays[] = $member->toArray(); } // IfNo/NoneMember, ReturnEmptyArray; OtherwiseReturnTwo-dimensionalArrayformat if (empty($memberArrays)) { return []; } return $memberArrays; } /** * getMemberlist. * * @return ProjectMemberItemDTO[]*/ public function getMembers(): array { return $this->members; } /** * setMemberlist. * * @param ProjectMemberItemDTO[] $members*/ public function setMembers(array $members): self { $this->members = $members; return $this; } /** * addMember.*/ public function addMember(ProjectMemberItemDTO $member): self { $this->members[] = $member; return $this; } /** * getMemberTotal.*/ public function getMemberCount(): int { return count($this->members); } /** * CheckwhetherForEmpty.*/ public function isEmpty(): bool { return empty($this->members); } } 
