<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Interfaces\BeAgent\DTO\Response; use JsonSerializable; class TopicMessagesResponseDTO implements JsonSerializable { /** * @var array Messagelist*/ protected array $list = []; /** * @var int totalRecord count */ protected int $total = 0; /** * @var int CurrentPage number*/ protected int $page = 1; /** * ConstructFunction. * * @param array $list Messagelist * @param int $total totalRecord count  * @param int $page CurrentPage number*/ public function __construct(array $list = [], int $total = 0, int $page = 1) { $this->list = $list; $this->total = $total; $this->page = $page; } /** * getMessagelist.*/ public function getList(): array { return $this->list; } /** * gettotalRecord count .*/ public function getTotal(): int { return $this->total; } /** * getCurrentPage number.*/ public function getPage(): int { return $this->page; } /** * Convert toArray.*/ public function toArray(): array { return [ 'list' => $this->list, 'total' => $this->total, 'page' => $this->page, ]; } /** * SequenceConvert toJSON.*/ public function jsonSerialize(): array { return $this->toArray(); } } 
