<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Interfaces\BeAgent\DTO; use Delightful\BeDelightful\Domain\BeAgent\Entity\TaskMessageEntity; use Delightful\BeDelightful\Domain\BeAgent\Entity\ValueObject\MessageMetadata; use Delightful\BeDelightful\Domain\BeAgent\Entity\ValueObject\MessagePayload; use Delightful\BeDelightful\Domain\BeAgent\Entity\ValueObject\TokenUsageDetails; /** * TopicTaskMessageDTO.*/ class TopicTaskMessageDTO { /** * ConstructFunction. * * @param MessageMetadata $metadata Message element Data * @param MessagePayload $payload Messageload * @param Null|TokenUsageDetails $tokenUsageDetails Token UseDetails*/ public function __construct( private MessageMetadata $metadata, private MessagePayload $payload, private ?TokenUsageDetails $tokenUsageDetails = Null ) { } /** * fromMessageDataCreateDTOInstance. * * @param array $data MessageData*/ public static function fromArray(array $data): self { $metadata = isset($data['metadata']) && is_array($data['metadata']) ? MessageMetadata::fromArray($data['metadata']) : new MessageMetadata(); $payload = isset($data['payload']) && is_array($data['payload']) ? MessagePayload::fromArray($data['payload']) : new MessagePayload(); $tokenUsageDetails = isset($data['token_usage_details']) && is_array($data['token_usage_details']) ? TokenUsageDetails::fromArray($data['token_usage_details']) : Null; return new self($metadata, $payload, $tokenUsageDetails); } /** * getMessage element Data.*/ public function getMetadata(): MessageMetadata { return $this->metadata; } /** * setMessage element Data. * * @param MessageMetadata $metadata Message element Data*/ public function setMetadata(MessageMetadata $metadata): self { $this->metadata = $metadata; return $this; } /** * getMessageload.*/ public function getPayload(): MessagePayload { return $this->payload; } /** * setMessageload. * * @param MessagePayload $payload Messageload*/ public function setPayload(MessagePayload $payload): self { $this->payload = $payload; return $this; } /** * get Token UseDetails.*/ public function getTokenUsageDetails(): ?TokenUsageDetails { return $this->tokenUsageDetails; } /** * set Token UseDetails. * * @param Null|TokenUsageDetails $tokenUsageDetails Token UseDetails*/ public function setTokenUsageDetails(?TokenUsageDetails $tokenUsageDetails): self { $this->tokenUsageDetails = $tokenUsageDetails; return $this; } /** * Convert toArray.*/ public function toArray(): array { return [ 'metadata' => $this->metadata->toArray(), 'payload' => $this->payload->toArray(), 'token_usage_details' => $this->tokenUsageDetails?->toArray(), ]; } /** * Convert toTaskMessageEntityEntity. * * @param int $topicId TopicID * @return TaskMessageEntity TaskMessageEntity*/ public function toTaskMessageEntity(int $topicId, string $senderUid, string $receiverUid): TaskMessageEntity { $messageData = [ 'sender_type' => 'assistant', 'sender_uid' => $senderUid, 'receiver_uid' => $receiverUid, 'message_id' => $this->payload->getMessageId() ?? '', 'type' => $this->payload->getType() ?? '', 'task_id' => $this->payload->getTaskId() ?? '', 'topic_id' => $topicId, 'status' => $this->payload->getStatus() ?? 'pending', 'content' => $this->payload->getContent() ?? '', 'raw_content' => '', 'steps' => $this->payload->getSteps() ?? Null, 'tool' => $this->payload->getTool() ?? Null, 'attachments' => $this->payload->getAttachments() ?? Null, 'mentions' => Null, 'event' => $this->payload->getEvent() ?? '', 'send_timestamp' => $this->payload->getSendTimestamp() ?? time(), 'show_in_ui' => $this->payload->getShowInUi() ?? true, 'seq_id' => $this->payload->getSeqId() ?? 0, 'correlation_id' => $this->payload->getCorrelationId(), ]; return new TaskMessageEntity($messageData); } } 
