<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Interfaces\BeAgent\Facade; use App\Domain\Contact\Entity\ValueObject\DataIsolation; use App\Domain\Contact\Entity\ValueObject\UserType; use App\ErrorCode\AgentErrorCode; use App\ErrorCode\GenericErrorCode; use App\Infrastructure\Core\Exception\ExceptionBuilder; use App\Infrastructure\Util\Context\RequestContext; use App\Interfaces\Authorization\Web\DelightfulUserAuthorization; use Delightful\ApiResponse\Annotation\ApiResponse; use Delightful\BeDelightful\Application\BeAgent\DTO\UserMessageDTO; use Delightful\BeDelightful\Application\BeAgent\Service\AgentAppService; use Delightful\BeDelightful\Application\BeAgent\Service\HandleTaskMessageAppService; use Delightful\BeDelightful\Application\BeAgent\Service\ProjectAppService; use Delightful\BeDelightful\Application\BeAgent\Service\TopicAppService; use Delightful\BeDelightful\Application\BeAgent\Service\TopicTaskAppService; use Delightful\BeDelightful\Application\BeAgent\Service\WorkspaceAppService; use Delightful\BeDelightful\Domain\BeAgent\Service\UserDomainService; use Delightful\BeDelightful\Interfaces\BeAgent\DTO\Request\CreateProjectRequestDTO; use Delightful\BeDelightful\Interfaces\BeAgent\DTO\Request\InitSandboxRequestDTO; use Delightful\BeDelightful\Interfaces\BeAgent\DTO\Request\SaveTopicRequestDTO; use Delightful\BeDelightful\Interfaces\BeAgent\DTO\Request\SaveWorkspaceRequestDTO; use Delightful\BeDelightful\Interfaces\BeAgent\DTO\Request\UpgradeSandboxRequestDTO; use Delightful\BeDelightful\Interfaces\BeAgent\DTO\Response\InitSandboxResponseDTO; use Hyperf\HttpServer\Contract\RequestInterface; class SandboxApi extends AbstractApi { public function __construct( protected RequestInterface $request, protected WorkspaceAppService $workspaceAppService, protected TopicTaskAppService $topicTaskAppService, protected HandleTaskMessageAppService $taskAppService, protected ProjectAppService $projectAppService, protected TopicAppService $topicAppService, protected UserDomainService $userDomainService, protected HandleTaskMessageAppService $handleTaskMessageAppService, protected AgentAppService $agentAppService, ) { parent::__construct($request); } #[ApiResponse('low_code')] public function getSandboxStatus(RequestContext $requestContext): array { $requestContext->setUserAuthorization($this->getAuthorization()); $topicId = $this->request->input('topic_id', ''); if (empty($topicId)) { ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'topic_id is required'); } // var_dump($this->getAuthorization(),"requestContext====="); $topic = $this->topicAppService->getTopicById((int) $topicId); $sandboxId = $topic->getSandboxId(); if (empty($sandboxId)) { ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'sandbox_id is required'); } $result = $this->agentAppService->getSandboxStatus($sandboxId); if (! $result->isSuccess()) { ExceptionBuilder::throw(AgentErrorCode::SANDBOX_NOT_FOUND, $result->getMessage()); } return $result->toArray(); } // Create aTask, Supportagent、tool、customthree modes, authenticationUseapi-key enter Rowauthentication #[ApiResponse('low_code')] public function initSandboxByApiKey(RequestContext $requestContext, InitSandboxRequestDTO $requestDTO): array { // fromRequestCreate inDTOAndValidateParameter $requestDTO = InitSandboxRequestDTO::fromRequest($this->request); // fromRequestCreate inDTO $apiKey = $this->getApiKey(); if (empty($apiKey)) { ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'The api key of header is required'); } // $userInfoRequestDTO = new UserInfoRequestDTO(['uid' => $apiKey]); // Judgerequest headerwhether store indelightful-user-id $delightfulUserId = $this->request->header('delightful-user-id', ''); $userEntity = $this->handleTaskMessageAppService->getUserAuthorization($apiKey, $delightfulUserId); if (empty($userEntity)) { ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'user_not_found'); } $delightfulUserAuthorization = DelightfulUserAuthorization::fromUserEntity($userEntity); $requestContext->setUserAuthorization($delightfulUserAuthorization); return $this->initSandbox($requestContext, $requestDTO, $delightfulUserAuthorization); } public function initSandboxByAuthorization(RequestContext $requestContext): array { $topicId = $this->request->input('topic_id', ''); $requestContext->setUserAuthorization($this->getAuthorization()); if (empty($topicId)) { ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'topic_id is required'); } $topic = $this->topicAppService->getTopic($requestContext, (int) $topicId); $projectId = $topic->getProjectId(); $project = $this->projectAppService->getProjectNotUserId((int) $projectId); $workspaceId = (string) $project->getWorkspaceId(); $requestDTO = new InitSandboxRequestDTO(); $requestDTO->setWorkspaceId($workspaceId); $requestDTO->setProjectId($projectId); $requestDTO->setTopicId($topicId); $requestDTO->setTopicMode($topic->getTopicMode()); return $this->initSandbox($requestContext, $requestDTO, $this->getAuthorization()); } public function initSandbox(RequestContext $requestContext, InitSandboxRequestDTO $requestDTO, $delightfulUserAuthorization): array { // Judge workspacewhether store in, Does not existin then InitializeWorkspace $this->initWorkspace($requestContext, $requestDTO); // JudgeProjectwhether store in, Does not existin then InitializeProject $this->initProject($requestContext, $requestDTO, $delightfulUserAuthorization->getId()); // JudgeTopicwhether store in, Does not existin then InitializeTopic $this->initTopic($requestContext, $requestDTO); // $requestDTO->setConversationId($requestDTO->getTopicId()); $initSandboxResponseDTO = new InitSandboxResponseDTO(); $initSandboxResponseDTO->setWorkspaceId($requestDTO->getWorkspaceId()); $initSandboxResponseDTO->setProjectId($requestDTO->getProjectId()); $initSandboxResponseDTO->setProjectMode($requestDTO->getProjectMode()); $initSandboxResponseDTO->setTopicId($requestDTO->getTopicId()); $initSandboxResponseDTO->setChatTopicId($requestDTO->getChatTopicId()); // $initSandboxResponseDTO->setConversationId($requestDTO->getTopicId()); $dataIsolation = new DataIsolation(); $dataIsolation->setCurrentUserId((string) $delightfulUserAuthorization->getId()); $dataIsolation->setThirdPartyOrganizationCode($delightfulUserAuthorization->getOrganizationCode()); $dataIsolation->setCurrentOrganizationCode($delightfulUserAuthorization->getOrganizationCode()); $dataIsolation->setUserType(UserType::Human); // $dataIsolation = new DataIsolation($userEntity->getId(), $userEntity->getOrganizationCode(), $userEntity->getWorkDir()); $userMessage = [ 'chat_topic_id' => $requestDTO->getChatTopicId(), 'topic_id' => (int) $requestDTO->getTopicId(), // 'chat_conversation_id' => $requestDTO->getConversationId(), 'prompt' => $requestDTO->getPrompt(), 'attachments' => Null, 'mentions' => Null, 'agent_user_id' => (string) $delightfulUserAuthorization->getId(), 'project_mode' => $requestDTO->getProjectMode(), 'topic_mode' => $requestDTO->getTopicMode(), 'task_mode' => '', 'model_id' => $requestDTO->getModelId(), ]; $userMessageDTO = UserMessageDTO::fromArray($userMessage); // $this->handleApiMessageAppService->handleApiMessage($dataIsolation, $userMessageDTO); // $userMessageDTO->setAgentMode($requestDTO->getProjectMode()); $result = $this->handleTaskMessageAppService->initSandbox($dataIsolation, $userMessageDTO); $initSandboxResponseDTO->setSandboxId($result['sandbox_id']); $initSandboxResponseDTO->setTaskId($result['task_id']); return $initSandboxResponseDTO->toArray(); } public function initWorkspace(RequestContext $requestContext, InitSandboxRequestDTO &$requestDTO) { // Judge workspacewhether store in, Does not existin then InitializeWorkspace $workspaceId = $requestDTO->getWorkspaceId(); if ($workspaceId > 0) { $workspace = $this->workspaceAppService->getWorkspaceDetail($requestContext, (int) $workspaceId); if (empty($workspace->getId())) { // throwAbnormal, WorkspaceDoes not existin ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'workspace_not_found'); } } else { $saveWorkspaceRequestDTO = new SaveWorkspaceRequestDTO(); $saveWorkspaceRequestDTO->setWorkspaceName('DefaultWorkspace'); $workspace = $this->workspaceAppService->createWorkspace($requestContext, $saveWorkspaceRequestDTO); $workspaceId = $workspace->getId(); } $requestDTO->setWorkspaceId($workspaceId); } public function initProject(RequestContext $requestContext, InitSandboxRequestDTO &$requestDTO, string $userId): void { // JudgeProjectwhether store in, Does not existin then InitializeProject $projectId = $requestDTO->getProjectId(); if ($projectId > 0) { $project = $this->projectAppService->getProject($requestContext, (int) $projectId); if (empty($project->getId())) { // throwAbnormal, ProjectDoes not existin ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'project_not_found'); } } else { $saveProjectRequestDTO = new CreateProjectRequestDTO(); $saveProjectRequestDTO->setProjectName('DefaultProject'); $saveProjectRequestDTO->setWorkspaceId((string) $requestDTO->getWorkspaceId()); $saveProjectRequestDTO->setProjectMode($requestDTO->getProjectMode()); $project = $this->projectAppService->createProject($requestContext, $saveProjectRequestDTO); if (! empty($project['project'])) { $projectId = $project['project']['id']; } else { ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'project_not_found'); } } $requestDTO->setProjectId($projectId); } public function initTopic(RequestContext $requestContext, InitSandboxRequestDTO &$requestDTO): void { // JudgeTopicwhether store in, Does not existin then InitializeTopic $topicId = $requestDTO->getTopicId(); if ($topicId > 0) { $topic = $this->topicAppService->getTopic($requestContext, (int) $topicId); if (empty($topic->getId())) { // throwAbnormal, TopicDoes not existin ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'topic_not_found'); } $chatTopicId = $topic->getChatTopicId(); } else { $saveTopicRequestDTO = new SaveTopicRequestDTO(); $saveTopicRequestDTO->setTopicName('DefaultTopic'); $saveTopicRequestDTO->setProjectId((string) $requestDTO->getProjectId()); $saveTopicRequestDTO->setWorkspaceId((string) $requestDTO->getWorkspaceId()); $saveTopicRequestDTO->setProjectMode($requestDTO->getProjectMode()); $saveTopicRequestDTO->setTopicMode($requestDTO->getTopicMode()); $topic = $this->topicAppService->createTopicNotValidateAccessibleProject($requestContext, $saveTopicRequestDTO); if (! empty($topic->getId())) { $topicId = $topic->getId(); $chatTopicId = $topic->getChatTopicId(); } else { ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'topic_not_found'); } } $requestDTO->setChatTopicId($chatTopicId); $requestDTO->setTopicId($topicId); } /** * Upgrade sandbox image.*/ #[ApiResponse('low_code')] public function upgradeSandbox(RequestContext $requestContext): array { $requestContext->setUserAuthorization($this->getAuthorization()); // ValidateRequestParameter $requestDTO = UpgradeSandboxRequestDTO::fromRequest($this->request); $messageId = $requestDTO->getMessageId(); $contextType = $requestDTO->getContextType(); if (empty($messageId)) { ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'message_id is required'); } // CreateDataIsolationObject $dataIsolation = new DataIsolation(); $dataIsolation->setCurrentUserId((string) $this->getAuthorization()->getId()); $dataIsolation->setCurrentOrganizationCode($this->getAuthorization()->getOrganizationCode()); $dataIsolation->setThirdPartyOrganizationCode($this->getAuthorization()->getOrganizationCode()); $dataIsolation->setUserType(UserType::Human); // callApplicationServiceExecuteUpgrade $result = $this->agentAppService->upgradeSandbox($dataIsolation, $messageId, $contextType); if (! $result->isSuccess()) { ExceptionBuilder::throw(AgentErrorCode::SANDBOX_UPGRADE_FAILED, $result->getMessage()); } return $result->toArray(); } } 
