<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Interfaces\BeAgent\Facade; use App\ErrorCode\GenericErrorCode; use App\Infrastructure\Core\Exception\BusinessException; use App\Infrastructure\Core\Exception\ExceptionBuilder; use App\Infrastructure\Util\Context\RequestContext; use App\Infrastructure\Util\ShadowCode\ShadowCode; use BeDelightful\ApiResponse\Annotation\ApiResponse; use BeDelightful\BeDelightful\Application\BeAgent\Service\AgentAppService; use BeDelightful\BeDelightful\Application\BeAgent\Service\FileConverterAppService; use BeDelightful\BeDelightful\Application\BeAgent\Service\HandleTaskMessageAppService; use BeDelightful\BeDelightful\Application\BeAgent\Service\ProjectAppService; use BeDelightful\BeDelightful\Application\BeAgent\Service\TaskAppService; use BeDelightful\BeDelightful\Application\BeAgent\Service\TopicAppService; use BeDelightful\BeDelightful\Application\BeAgent\Service\TopicTaskAppService; use BeDelightful\BeDelightful\Application\BeAgent\Service\WorkspaceAppService; use BeDelightful\BeDelightful\Domain\BeAgent\Constant\ConvertStatusEnum; use BeDelightful\BeDelightful\Domain\BeAgent\Service\UserDomainService; use BeDelightful\BeDelightful\ErrorCode\BeAgentErrorCode; use BeDelightful\BeDelightful\Interfaces\BeAgent\DTO\Request\ConvertFilesRequestDTO; use BeDelightful\BeDelightful\Interfaces\BeAgent\DTO\Request\GetTaskFilesRequestDTO; use BeDelightful\BeDelightful\Interfaces\BeAgent\DTO\TopicTaskMessageDTO; use Hyperf\HttpServer\Contract\RequestInterface; use Hyperf\Logger\LoggerFactory; use Psr\Log\LoggerInterface; use Qbhy\HyperfAuth\AuthManager; use Throwable; #[ApiResponse('low_code')] class TaskApi extends AbstractApi { protected LoggerInterface $logger; public function __construct( protected RequestInterface $request, protected WorkspaceAppService $workspaceAppService, protected TopicTaskAppService $topicTaskAppService, protected HandleTaskMessageAppService $handleTaskAppService, protected TaskAppService $taskAppService, protected FileConverterAppService $fileConverterAppService, LoggerFactory $loggerFactory, protected ProjectAppService $projectAppService, protected TopicAppService $topicAppService, protected UserDomainService $userDomainService, protected HandleTaskMessageAppService $handleTaskMessageAppService, protected AgentAppService $agentAppService, ) { $this->logger = $loggerFactory->get(get_class($this)); parent::__construct($request); } /** * DeliverTopicTaskMessage. * * @param RequestContext $requestContext request context * @return array OperationResult * @throws BusinessException throws exception if parameters are invalid or operation fails*/ public function deliverMessage(RequestContext $requestContext): array { // from header get from token field $token = $this->request->header('token', ''); if (empty($token)) { ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'token_required'); } // from env getSandbox token, thencompareSandbox token AndRequest token whetherConsistent $sandboxToken = config('be-delightful.sandbox.token', ''); if ($sandboxToken !== $token) { ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'token_invalid'); } // ViewwhetherObfuscate $isConfusion = $this->request->input('obfuscated', false); if ($isConfusion) { // ObfuscateProcess/Handle $rawData = ShadowCode::unShadow($this->request->input('data', '')); } else { $rawData = $this->request->input('data', ''); } $requestData = json_decode($rawData, true); // fromRequestCreate inDTO $messageDTO = TopicTaskMessageDTO::fromArray($requestData); // callApplicationService enter RowMessageDeliver if (config('be-delightful.message.process_mode') === 'direct') { return $this->topicTaskAppService->handleTopicTaskMessage($messageDTO); } return $this->topicTaskAppService->deliverTopicTaskMessage($messageDTO); } public function resumeTask(RequestContext $requestContext): array { // from header get from token field $token = $this->request->header('token', ''); if (empty($token)) { ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'token_required'); } // from env getSandbox token, thencompareSandbox token AndRequest token whetherConsistent $sandboxToken = config('be-delightful.sandbox.token', ''); if ($sandboxToken !== $token) { ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'token_invalid'); } $sandboxId = $this->request->input('sandbox_id', ''); $isInit = $this->request->input('is_init', false); $this->taskAppService->sendContinueMessageToSandbox($sandboxId, $isInit); return []; } /** * getTask next AllAttachment. * * @param RequestContext $requestContext request context * @return array AttachmentlistandPaginationinformation * @throws BusinessException IfParameterInvalidThen throwAbnormal*/ public function getTaskAttachments(RequestContext $requestContext): array { // set user authorization information $requestContext->setUserAuthorization($this->getAuthorization()); $userAuthorization = $requestContext->getUserAuthorization(); // getTaskFileRequestDTO $dto = GetTaskFilesRequestDTO::fromRequest($this->request); // callApplicationService return $this->workspaceAppService->getTaskAttachments( $userAuthorization, $dto->getId(), $dto->getPage(), $dto->getPageSize() ); } /** * BatchConvertFile. * * @param RequestContext $requestContext request context * @return array ConvertResult*/ public function convertFiles(RequestContext $requestContext): array { // getRequestDTO $dto = ConvertFilesRequestDTO::fromRequest($this->request); // set user authorization information $requestContext->setUserAuthorization(di(AuthManager::class)->guard(name: 'web')->user()); $userAuthorization = $requestContext->getUserAuthorization(); try { // callApplicationService return $this->fileConverterAppService->convertFiles($userAuthorization, $dto); } catch (Throwable $e) { $this->logger->error('Convert files API failed', [ 'user_id' => $userAuthorization->getId(), 'organization_code' => $userAuthorization->getOrganizationCode(), 'project_id' => $dto->project_id, 'file_ids_count' => count($dto->file_ids), 'convert_type' => $dto->convert_type, 'error' => $e->getMessage(), 'file' => $e->getFile(), 'line' => $e->getLine(), 'trace' => $e->getTraceAsString(), ]); throw $e; } } /** * CheckFileConvertStatus. * * @param RequestContext $requestContext request context * @return array StatusCheckResult*/ public function checkFileConvertStatus(RequestContext $requestContext): array { $taskKey = $this->request->input('task_key'); if (empty($taskKey)) { ExceptionBuilder::throw(BeAgentErrorCode::VALIDATE_FAILED, 'validation.required'); } // set user authorization information $requestContext->setUserAuthorization(di(AuthManager::class)->guard(name: 'web')->user()); $userAuthorization = $requestContext->getUserAuthorization(); try { $result = $this->fileConverterAppService->checkFileConvertStatus($userAuthorization, $taskKey); // IfStatus is  FAILED, ThrowAbnormal if ($result->getStatus() === ConvertStatusEnum::FAILED->value) { $this->logger->error('File conversion failed', [ 'task_key' => $taskKey, 'user_id' => $userAuthorization->getId(), 'organization_code' => $userAuthorization->getOrganizationCode(), 'status' => $result->getStatus(), 'total_files' => $result->getTotalFiles(), 'success_count' => $result->getSuccessCount(), 'batch_id' => $result->getBatchId(), 'convert_type' => $result->getConvertType(), ]); ExceptionBuilder::throw(BeAgentErrorCode::FILE_CONVERT_FAILED, 'file.convert_failed'); } // IfStatus is  COMPLETED butNo/NoneDownloadAddress, DescriptionTaskhappen ed Error if ($result->getStatus() === ConvertStatusEnum::COMPLETED->value && empty($result->getDownloadUrl())) { $this->logger->error('File conversion completed but no download URL available', [ 'task_key' => $taskKey, 'user_id' => $userAuthorization->getId(), 'organization_code' => $userAuthorization->getOrganizationCode(), 'status' => $result->getStatus(), 'total_files' => $result->getTotalFiles(), 'success_count' => $result->getSuccessCount(), 'batch_id' => $result->getBatchId(), 'convert_type' => $result->getConvertType(), ]); ExceptionBuilder::throw(BeAgentErrorCode::FILE_CONVERT_FAILED, 'file.convert_failed'); } return $result->toArray(); } catch (Throwable $e) { $this->logger->error('Check file convert status failed', [ 'task_key' => $taskKey, 'user_id' => $userAuthorization->getId(), 'organization_code' => $userAuthorization->getOrganizationCode(), 'error' => $e->getMessage(), 'file' => $e->getFile(), 'line' => $e->getLine(), 'trace' => $e->getTraceAsString(), ]); throw $e; } } } 