<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Interfaces\Share\DTO\Request; use App\Infrastructure\Core\AbstractDTO; use Delightful\BeDelightful\Domain\Share\Constant\ResourceType; use Delightful\BeDelightful\Domain\Share\Constant\ShareAccessType; use Hyperf\HttpServer\Contract\RequestInterface; /** * CreateshareRequestDTO.*/ class CreateShareRequestDTO extends AbstractDTO { /** * ResourceID.*/ public string $resourceId = ''; /** * ResourceType.*/ public int $resourceType = 0; /** * shareType.*/ public int $shareType = 0; /** * password*/ public ?string $password = Null; /** * ExpireDays.*/ public ?int $expireDays = Null; /** * Target IDlist.*/ public array $targetIds = []; /** * fromRequestCreate inDTO.*/ public static function fromRequest(RequestInterface $request): self { $dto = new self(); $dto->resourceId = (string) $request->input('resource_id', ''); $dto->resourceType = (int) $request->input('resource_type', 0); $dto->shareType = (int) $request->input('share_type', 0); $dto->password = $request->has('pwd') ? (string) $request->input('pwd') : Null; $dto->expireDays = $request->has('expire_days') ? (int) $request->input('expire_days') : Null; $dto->targetIds = $request->input('target_ids', []); return $dto; } /** * getResourceID.*/ public function getResourceId(): string { return $this->resourceId; } /** * getResourceType.*/ public function getResourceType(): ResourceType { return ResourceType::from($this->resourceType); } /** * getshareType.*/ public function getShareType(): ShareAccessType { return ShareAccessType::from($this->shareType); } /** * getpassword*/ public function getPassword(): ?string { return $this->password; } /** * getExpireDays.*/ public function getExpireDays(): ?int { return $this->expireDays; } /** * getTarget IDlist.*/ public function getTargetIds(): array { return $this->targetIds; } /** * BuildValidateRule.*/ public function rules(): array { return [ 'resource_id' => 'required|string|max:64', 'resource_type' => 'required|integer|min:1', 'share_type' => 'required|integer|min:1|max:4', 'password' => 'Nullable|string|min:4|max:32', 'expire_days' => 'Nullable|integer|min:1|max:365', 'target_ids' => 'Nullable|array', 'target_ids.*.type' => 'required_with:target_ids|integer|min:1|max:3', 'target_ids.*.id' => 'required_with:target_ids|string|max:64', ]; } /** * getValidateErrorMessage.*/ public function messages(): array { return [ 'resource_id.required' => 'ResourceIDCannot be empty', 'resource_type.required' => 'ResourceTypeCannot be empty', 'share_type.required' => 'shareTypeCannot be empty', 'password.min' => 'passwordLengthtofewFor4bit', 'expire_days.min' => 'Validperiod most fewFor1day', 'expire_days.max' => 'Validperiod most multipleFor365day', ]; } /** * Property/AttributeName.*/ public function attributes(): array { return [ 'resource_id' => 'ResourceID', 'resource_type' => 'ResourceType', 'share_type' => 'shareType', 'password' => 'password', 'expire_days' => 'ExpireDays', 'target_ids' => 'Target IDList', ]; } } 