<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\SuperMagic\Interfaces\SuperAgent\DTO\Request; use Hyperf\Validation\Request\FormRequest; class CreateTaskRequestDTO extends FormRequest { /** * @var string ChatTopicID*/ protected string $chatTopicId = ''; /** * @var string TaskTipword*/ protected string $prompt = ''; /** * @var Null|string Attachmentinformation (JSONformat)*/ protected ?string $attachments = Null; /** * ValidateRule.*/ public function rules(): array { return [ 'chat_topic_id' => 'required|string', 'prompt' => 'required|string', 'attachments' => 'Nullable|string', ]; } /** * Property/AttributeName.*/ public function attributes(): array { return [ 'chat_topic_id' => 'ChatTopicID', 'prompt' => 'Tipword', 'attachments' => 'AttachmentInformation', ]; } public function getChatTopicId(): string { return $this->chatTopicId; } public function getPrompt(): string { return $this->prompt; } public function getAttachments(): ?string { return $this->attachments; } /** * PrepareData.*/ protected function prepareForValidation(): void { $this->chatTopicId = (string) $this->input('chat_topic_id', ''); $this->prompt = (string) $this->input('prompt', ''); $this->attachments = $this->has('attachments') ? (string) $this->input('attachments') : Null; } } 