ain style call*/ public function setPage(int $page): self { $this->page = $page; return $this; } /** * setPer pageQuantity. * * @param int $pageSize Per pageQuantity * @return self ReturnCurrentInstance, Supportchain style call*/ public function setPageSize(int $pageSize): self { $this->pageSize = $pageSize; return $this; } /** * setFile typeFilter. * * @param array $fileType File typeFilterArray * @return self ReturnCurrentInstance, Supportchain style call*/ public function setFileType(array $fileType): self { $this->fileType = $fileType; return $this; } /** * getaccessedtoken. * * @return Null|string accessedtoken*/ public function getToken(): ?string { return $this->token; } /** * setaccessedtoken. * * @param Null|string $token accessedtoken * @return self ReturnCurrentInstance, Supportchain style call*/ public function setToken(?string $token): self { $this->token = $token; return $this; } } 