<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\SuperDelightful\Interfaces\SuperAgent\DTO\Response; use Delightful\SuperDelightful\Domain\Share\Entity\ResourceShareEntity; use Delightful\SuperDelightful\Domain\Share\Service\ResourceShareDomainService; /** * Invitation linkResponseDTO. * * Used forReturnInvitation linkBasicinformation*/ class InvitationLinkResponseDTO { public function __construct( public readonly string $id, public readonly string $projectId, public readonly string $token, public readonly bool $isEnabled, public readonly bool $isPasswordEnabled, public readonly ?string $password, public readonly string $defaultJoinPermission, public readonly string $createdBy, public readonly string $createdAt, ) { } /** * fromResourceShareEntityCreateDTO.*/ public static function fromEntity( ResourceShareEntity $shareEntity, ResourceShareDomainService $resourceShareDomainService ): self { // from extra fieldget default_join_permission $defaultJoinPermission = $shareEntity->getExtraAttribute('default_join_permission', 'viewer'); return new self( id: (string) $shareEntity->getId(), projectId: $shareEntity->getResourceId(), token: $shareEntity->getShareCode(), isEnabled: $shareEntity->getIsEnabled(), isPasswordEnabled: $shareEntity->getIsPasswordEnabled(), password: $resourceShareDomainService->getDecryptedPassword($shareEntity), defaultJoinPermission: $defaultJoinPermission, createdBy: $shareEntity->getCreatedUid(), createdAt: $shareEntity->getCreatedAt(), ); } /** * Convert toArray.*/ public function toArray(): array { return [ 'id' => $this->id, 'project_id' => $this->projectId, 'token' => $this->token, 'is_enabled' => $this->isEnabled, 'is_password_enabled' => $this->isPasswordEnabled, 'password' => $this->isPasswordEnabled ? $this->password : '', 'default_join_permission' => $this->defaultJoinPermission, 'created_by' => $this->createdBy, 'created_at' => $this->createdAt, ]; } } 