<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\SuperDelightful\Interfaces\SuperAgent\DTO\Response; use App\Infrastructure\Core\AbstractDTO; class StatusMetricsDTO extends AbstractDTO { /** * @var int ErrorStatusTopicQuantity*/ protected int $errorCount = 0; /** * @var int CompleteStatusTopicQuantity*/ protected int $completedCount = 0; /** * @var int Run in StatusTopicQuantity*/ protected int $runningCount = 0; /** * @var int Wait in StatusTopicQuantity*/ protected int $waitingCount = 0; /** * @var int  already PauseStatusTopicQuantity*/ protected int $pausedCount = 0; /** * getErrorStatusTopicQuantity.*/ public function getErrorCount(): int { return $this->errorCount; } /** * setErrorStatusTopicQuantity.*/ public function setErrorCount(int $errorCount): self { $this->errorCount = $errorCount; return $this; } /** * getCompleteStatusTopicQuantity.*/ public function getCompletedCount(): int { return $this->completedCount; } /** * setCompleteStatusTopicQuantity.*/ public function setCompletedCount(int $completedCount): self { $this->completedCount = $completedCount; return $this; } /** * getRun in StatusTopicQuantity.*/ public function getRunningCount(): int { return $this->runningCount; } /** * setRun in StatusTopicQuantity.*/ public function setRunningCount(int $runningCount): self { $this->runningCount = $runningCount; return $this; } /** * getWait in StatusTopicQuantity.*/ public function getWaitingCount(): int { return $this->waitingCount; } /** * setWait in StatusTopicQuantity.*/ public function setWaitingCount(int $waitingCount): self { $this->waitingCount = $waitingCount; return $this; } /** * get already PauseStatusTopicQuantity.*/ public function getPausedCount(): int { return $this->pausedCount; } /** * set already PauseStatusTopicQuantity.*/ public function setPausedCount(int $pausedCount): self { $this->pausedCount = $pausedCount; return $this; } /** * Convert toArray.*/ public function toArray(): array { return [ 'error_count' => $this->errorCount, 'completed_count' => $this->completedCount, 'running_count' => $this->runningCount, 'waiting_count' => $this->waitingCount, 'paused_count' => $this->pausedCount, ]; } } 