<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Interfaces\SuperAgent\DTO\Response; use App\Infrastructure\Core\AbstractDTO; class TotalMetricsDTO extends AbstractDTO { /** * @var int userTotal*/ protected int $userCount = 0; /** * @var int TopicTotal*/ protected int $topicCount = 0; /** * getuserTotal.*/ public function getUserCount(): int { return $this->userCount; } /** * setuserTotal.*/ public function setUserCount(int $userCount): self { $this->userCount = $userCount; return $this; } /** * getTopicTotal.*/ public function getTopicCount(): int { return $this->topicCount; } /** * setTopicTotal.*/ public function setTopicCount(int $topicCount): self { $this->topicCount = $topicCount; return $this; } /** * Convert toArray.*/ public function toArray(): array { return [ 'user_count' => $this->userCount, 'topic_count' => $this->topicCount, ]; } } 