<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\BeDelightful\Interfaces\SuperAgent\Facade; use App\ErrorCode\GenericErrorCode; use App\Infrastructure\Core\Exception\ExceptionBuilder; use App\Infrastructure\Core\Traits\DelightfulUserAuthorizationTrait; use Hyperf\HttpServer\Contract\RequestInterface; abstract class AbstractApi { use DelightfulUserAuthorizationTrait; public function __construct( protected RequestInterface $request, ) { } /** * Validate sandbox Token. */ protected function validateSandboxToken(): void { // from header get from token field $token = $this->request->getHeaderLine('token'); if (empty($token)) { ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'token_required'); } // from env getSandbox token, thencompareSandbox token AndRequest token whetherConsistent $sandboxToken = config('super-delightful.sandbox.token', ''); if ($sandboxToken !== $token) { ExceptionBuilder::throw(GenericErrorCode::ParameterMissing, 'token_invalid'); } } protected function getAccessToken(): string { // fully compatible openai api_key format // 1. in ordertryfromrequest headerget from $headers = [ 'api-key', 'llm-access-token', ]; $token = $this->getTokenFromHeaders($headers); if (! empty($token)) { return $token; } // 2. from Authorization headerget from Bearer token $token = $this->getTokenFromBearerAuth(); if (! empty($token)) { return $token; } // 3. from HTTP Basic Auth get from token $token = $this->getTokenFromBasicAuth(); if (! empty($token)) { return $token; } // 4. fromquery parameterget from $apiKey = $this->request->query('api_key'); if (! empty($apiKey)) { return $apiKey; } // 5. fromrequest bodyget from $parsedBody = $this->request->getParsedBody(); if (is_array($parsedBody) && isset($parsedBody['api_key'])) { return $parsedBody['api_key']; } return ''; } /** * fromspecifiedrequest headerlist in in ordergettoken.*/ protected function getTokenFromHeaders(array $headerNames): string { foreach ($headerNames as $headerName) { if (! empty($this->request->getHeader($headerName))) { return $this->request->getHeader($headerName)[0]; } } return ''; } /** * from Authorization headerget from Bearer token.*/ protected function getTokenFromBearerAuth(): string { if (! empty($this->request->getHeader('authorization'))) { $authHeader = $this->request->getHeader('authorization')[0] ?? ''; if (str_starts_with(strtolower($authHeader), 'bearer ')) { return substr($authHeader, 7); } } return ''; } /** * from HTTP Basic Auth get from token. */ protected function getTokenFromBasicAuth(): string { if (! empty($this->request->getHeader('php-auth-user'))) { return $this->request->getHeader('php-auth-user')[0]; } return ''; } protected function getClientIps(): array { $serverParams = $this->request->getServerParams(); $ips = []; $ipHeaders = ['x-forwarded-for', 'x-real-ip']; foreach ($ipHeaders as $header) { foreach ($this->request->getHeader($header) as $item) { $ips[] = trim($item); } } if (! empty($serverParams['remote_addr'])) { $ip = trim(explode(':', $serverParams['remote_addr'], 2)[0]); if (! empty($ip)) { $ips = array_merge($ips, [$ip]); } } return $ips; } protected function getApiKey(): string { $headers = [ 'API-KEY', 'api_key', 'api-key', 'API_KEY', ]; $token = $this->getTokenFromHeaders($headers); if (! empty($token)) { return $token; } return ''; } } 