<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Dtyq\SuperMagic\Application\SuperAgent\Service; use Dtyq\SuperMagic\Infrastructure\ExternalAPI\SandboxOS\Exception\SandboxOperationException; use Dtyq\SuperMagic\Infrastructure\ExternalAPI\SandboxOS\Gateway\Result\SandboxStatusResult; use Dtyq\SuperMagic\Infrastructure\ExternalAPI\SandboxOS\Gateway\SandboxGatewayInterface; use Hyperf\Logger\LoggerFactory; use Psr\Log\LoggerInterface; /** * AgentMessageApplicationService * Provide advancedAgentGeneral message Function, Including automaticInitializeAndStatusmanagement.*/ class AgentFileAppService { private LoggerInterface $logger; public function __construct( LoggerFactory $loggerFactory, private readonly SandboxGatewayInterface $gateway, ) { $this->logger = $loggerFactory->get('sandbox'); } /** * getSandboxStatus * * @param string $sandboxId SandboxID * @return SandboxStatusResult SandboxStatusResult*/ public function getSandboxStatus(string $sandboxId): SandboxStatusResult { $this->logger->info('[Sandbox][App] Getting sandbox status', [ 'sandbox_id' => $sandboxId, ]); $result = $this->gateway->getSandboxStatus($sandboxId); if (! $result->isSuccess()) { $this->logger->error('[Sandbox][App] Failed to get sandbox status', [ 'sandbox_id' => $sandboxId, 'error' => $result->getMessage(), 'code' => $result->getCode(), ]); throw new SandboxOperationException('Get sandbox status', $result->getMessage(), $result->getCode()); } $this->logger->info('[Sandbox][App] Sandbox status retrieved', [ 'sandbox_id' => $sandboxId, 'status' => $result->getStatus(), ]); return $result; } } 