<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Dtyq\SuperMagic\Domain\Agent\Entity\ValueObject; use Exception; enum SuperMagicAgentOptimizationType: string { case None = 'none'; case OptimizeNameDescription = 'optimize_name_description'; case OptimizeContent = 'optimize_content'; case OptimizeName = 'optimize_name'; case OptimizeDescription = 'optimize_description'; /** * getEnumDescription.*/ public function getDescription(): string { return match ($this) { self::OptimizeNameDescription => 'OptimizeNameAndDescription', self::OptimizeContent => 'OptimizeContent', self::OptimizeName => 'OptimizeName', self::OptimizeDescription => 'OptimizeDescription', self::None => throw new Exception('To be implemented'), }; } /** * getAllEnumValue.*/ public static function values(): array { return array_column(self::cases(), 'value'); } /** * fromStringCreateEnumInstance.*/ public static function fromString(string $value): self { $type = self::tryFrom($value); if ($type === Null) { return self::None; } return $type; } public function isNone(): bool { return $this === self::None; } } 