<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the software license */ namespace Dtyq\SuperMagic\Domain\Agent\Entity\ValueObject; enum SuperMagicAgentType: int { /** * Built-inAgent.*/ case Built_In = 1; /** * CustomAgent.*/ case Custom = 2; /** * getTypeDescription.*/ public function getDescription(): string { return match ($this) { self::Built_In => 'Built-in', self::Custom => 'Custom', }; } /** * whetherForBuilt-inType.*/ public function isBuiltIn(): bool { return $this === self::Built_In; } /** * whetherForCustomType.*/ public function isCustom(): bool { return $this === self::Custom; } /** * getAllAvailableEnumValue. * @return array<int>*/ public static function getAvailableValues(): array { return array_map(fn ($case) => $case->value, self::cases()); } /** * getAllAvailableEnumValueString (Used forValidateRule).*/ public static function getValidationRule(): string { return implode(',', self::getAvailableValues()); } } 