<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Dtyq\SuperMagic\Domain\Share\Constant; use RuntimeException; /** * ResourceTypeEnum.*/ enum ResourceType: int { // ExistingType case BotCode = 1; // AI helper case SubFlowCode = 2; // subProcess case ToolSet = 3; // ToolSet case Knowledge = 4; // knowidentify database  // New businessType case Topic = 5; // Topic case Document = 6; // Document case Schedule = 7; // dayprocess case MultiTable = 8; // MultidimensionalTable format  case Form = 9; // Tablesingle case MindMap = 10; // Mind map case Website = 11; // website case Project = 12; // Project case File = 13; // File case ProjectInvitation = 14; // ProjectInvitation link /** * getResourceTypeBusinessName.*/ public function getBusinessName(): string { return match ($this) { self::BotCode => 'bot', self::SubFlowCode => 'subflow', self::ToolSet => 'toolset', self::Knowledge => 'knowledge', self::Topic => 'topic', self::Document => 'document', self::Schedule => 'schedule', self::MultiTable => 'multitable', self::Form => 'form', self::MindMap => 'mindmap', self::Website => 'website', self::Project => 'project', self::File => 'file', self::ProjectInvitation => 'project_invitation', }; } /** * By/According toBusinessNamegetResourceTypeEnum. * * @param string $businessName BusinessName * @return ResourceType ResourceTypeEnum * @throws RuntimeException whenNot foundPair should ResourceTypeThrow whenAbnormal*/ public static function fromBusinessName(string $businessName): self { foreach (self::cases() as $type) { if ($type->getBusinessName() === $businessName) { return $type; } } throw new RuntimeException("Business not foundNameFor '{$businessName}' ResourceType"); } public static function isProjectInvitation(int $type): bool { return self::ProjectInvitation->value === $type; } } 