<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the software license */ namespace Dtyq\SuperMagic\Domain\Share\Constant; /** * shareaccessedTypeEnum.*/ enum ShareAccessType: int { case SelfOnly = 1; // Only self canaccessed case OrganizationInternal = 2; // OrganizationInternalcanaccessed case SpecificTarget = 3; // SpecifiedDepartment/Membercanaccessed case Internet = 4; // Internet canaccessed(Needlink) /** * getshareTypeDescription.*/ public function getDescription(): string { return match ($this) { self::SelfOnly => 'Only self canaccessed', self::OrganizationInternal => 'OrganizationInternalcanaccessed', self::SpecificTarget => 'SpecifiedDepartment/Membercanaccessed', self::Internet => 'Internet canaccessed', }; } /** * CheckwhetherNeedpasswordProtect.*/ public function needsPassword(): bool { return $this === self::Internet; } /** * CheckwhetherNeedspecifiedTarget.*/ public function needsTargets(): bool { return $this === self::SpecificTarget; } } 