<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the software license */ namespace Dtyq\SuperMagic\Domain\SuperAgent\Constant; use InvalidArgumentException; /** * TopiccopiedTaskStatusEnum.*/ enum DuplicateStatusEnum: string { case RUNNING = 'running'; case FINISHED = 'finished'; case ERROR = 'error'; /** * getAllValidStatusValue*/ public static function getValidStatuses(): array { return array_map(fn (self $case) => $case->value, self::cases()); } /** * CheckStatuswhetherValid.*/ public static function isValid(string $status): bool { return in_array($status, self::getValidStatuses(), true); } /** * fromStringCreateEnumInstance.*/ public static function fromString(string $status): self { return match ($status) { 'running' => self::RUNNING, 'finished' => self::FINISHED, 'error' => self::ERROR, default => throw new InvalidArgumentException("Invalid status: {$status}"), }; } } 