<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Dtyq\SuperMagic\Domain\SuperAgent\Entity\ValueObject; use App\Infrastructure\Core\Exception\ExceptionBuilder; use Dtyq\SuperMagic\ErrorCode\SuperAgentErrorCode; /** * MemberTypeValueObject * * encapsulateMemberTypebusiness logicAndValidateRule*/ enum MemberType: string { case USER = 'User'; case DEPARTMENT = 'Department'; /** * fromStringCreateInstance.*/ public static function fromString(string $type): self { return match ($type) { 'User' => self::USER, 'Department' => self::DEPARTMENT, default => ExceptionBuilder::throw(SuperAgentErrorCode::INVALID_MEMBER_TYPE) }; } /** * fromValueCreateInstance.*/ public static function fromValue(string $value): self { return self::from($value); } /** * getValue*/ public function getValue(): string { return $this->value; } /** * whetherForuserType.*/ public function isUser(): bool { return $this === self::USER; } /** * whetherForDepartmentType.*/ public function isDepartment(): bool { return $this === self::DEPARTMENT; } /** * getDescription.*/ public function getDescription(): string { return match ($this) { self::USER => 'User', self::DEPARTMENT => 'Department', }; } } 