<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\SuperMagic\Domain\SuperAgent\Repository\Model; use App\Infrastructure\Core\AbstractModel; /** * ProjectFileModel (originalTaskFileModel).*/ class ProjectFileModel extends AbstractModel { /** * softDeletefield.*/ public const DELETED_AT = 'deleted_at'; /** * Tablename.*/ protected ?string $table = 'magic_super_agent_project_files'; /** * Primary key.*/ protected string $primaryKey = 'file_id'; /** * Fillablefield.*/ protected array $fillable = [ 'user_id', 'organization_code', 'project_id', 'topic_id', 'task_id', 'file_type', 'file_name', 'file_extension', 'file_key', 'file_size', 'external_url', 'storage_type', 'is_hidden', ]; /** * fieldTypeConvert.*/ protected array $casts = [ 'file_id' => 'integer', 'project_id' => 'integer', 'topic_id' => 'integer', 'task_id' => 'integer', 'file_size' => 'integer', 'is_hidden' => 'integer', 'created_at' => 'datetime', 'updated_at' => 'datetime', 'deleted_at' => 'datetime', ]; /** * Datefield.*/ protected array $dates = [ 'created_at', 'updated_at', 'deleted_at', ]; /** * getFileBelong toProject.*/ public function project() { return $this->belongsTo(ProjectModel::class, 'project_id', 'id'); } /** * getFileBelong toTopic.*/ public function topic() { return $this->belongsTo(TopicModel::class, 'topic_id', 'id'); } /** * getFileBelong toTask*/ public function task() { return $this->belongsTo(TaskModel::class, 'task_id', 'id'); } } 