<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Dtyq\SuperMagic\Domain\SuperAgent\Repository\Model; use App\Infrastructure\Core\AbstractModel; use Hyperf\Database\Model\SoftDeletes; /** * TaskModel.*/ class TaskModel extends AbstractModel { use SoftDeletes; /** * Tablename.*/ protected ?string $table = 'magic_super_agent_task'; /** * Primary key.*/ protected string $primaryKey = 'id'; /** * Fillablefield.*/ protected array $fillable = [ 'id', 'user_id', 'workspace_id', 'project_id', 'topic_id', 'from_task_id', 'task_id', 'sandbox_id', 'prompt', 'attachments', 'mentions', 'task_status', 'work_dir', 'task_mode', 'err_msg', 'started_at', 'finished_at', 'created_at', 'updated_at', 'deleted_at', ]; /** * Datefield.*/ protected array $dates = [ 'started_at', 'finished_at', 'created_at', 'updated_at', 'deleted_at', ]; /** * getTaskBelong toWorkspace.*/ public function workspace() { return $this->belongsTo(WorkspaceModel::class, 'workspace_id', 'id'); } /** * getTaskBelong toProject.*/ public function project() { return $this->belongsTo(ProjectModel::class, 'project_id', 'id'); } /** * getTaskBelong toTopic.*/ public function topic() { return $this->belongsTo(TopicModel::class, 'topic_id', 'id'); } } 