<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\SuperMagic\Infrastructure\ExternalAPI\SandboxOS\AsrRecorder\Config; /** * ASR Streaming recognitionFileconfiguration * Used for finishTask InterfaceStreaming recognitionFileProcess/Handleconfiguration.*/ readonly class AsrTranscriptFileConfig { /** * @param string $sourcePath sourceFile path (each otherPairin workspace) * @param string $action OperationType (delete - directlyDelete)*/ public function __construct( private string $sourcePath, private string $action = 'delete' ) { } public function getSourcePath(): string { return $this->sourcePath; } public function getAction(): string { return $this->action; } /** * Convert toArray (Used for HTTP Request).*/ public function toArray(): array { return [ 'source_path' => $this->sourcePath, 'action' => $this->action, ]; } } 