<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the software license */ namespace Dtyq\SuperMagic\Infrastructure\ExternalAPI\SandboxOS\Gateway\Constant; /** * SandboxStatusconstant amount  * By/According toSandboxGeneral message DocumentdefineStatusValue*/ class SandboxStatus { /** * SandboxpendingStartStatus*/ public const PENDING = 'Pending'; /** * SandboxRun in Status*/ public const RUNNING = 'Running'; /** * Sandbox already exitoutputStatus*/ public const EXITED = 'Exited'; /** * SandboxUnknownStatus*/ public const UNKNOWN = 'Unknown'; /** * SandboxNot foundStatus*/ public const NOT_FOUND = 'NotFound'; /** * getAllValidStatus*/ public static function getAllStatuses(): array { return [ self::PENDING, self::RUNNING, self::EXITED, self::UNKNOWN, self::NOT_FOUND, ]; } /** * CheckStatuswhetherValid.*/ public static function isValidStatus(string $status): bool { return in_array($status, self::getAllStatuses(), true); } /** * CheckSandboxwhetherAvailable (Run in ).*/ public static function isAvailable(string $status): bool { return $status === self::RUNNING; } } 