<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Dtyq\SuperMagic\Interfaces\Share\DTO\Response; /** * withpasswordshareProjectDTO. * thisDTOOnly used when neededReturnpasswordspecificInterface.*/ class ShareItemWithPasswordDTO extends ShareItemDTO { /** * @var string sharepassword (plaintext)*/ public string $pwd = ''; /** * fromBaseDTOCreate withpasswordDTO. * * @param ShareItemDTO $baseDto BaseDTO * @param string $password Decrypt after password*/ public static function fromBaseDto(ShareItemDTO $baseDto, string $password): self { $dto = new self(); $dto->id = $baseDto->id; $dto->resourceId = $baseDto->resourceId; $dto->resourceType = $baseDto->resourceType; $dto->resourceTypeName = $baseDto->resourceTypeName; $dto->shareCode = $baseDto->shareCode; $dto->hasPassword = $baseDto->hasPassword; $dto->pwd = $password; $dto->shareType = $baseDto->shareType; return $dto; } /** * ConvertDTOConvert toArray. * * @return array associatedArray*/ public function toArray(): array { $data = parent::toArray(); $data['pwd'] = $this->pwd; return $data; } } 